INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(1, '', TIMESTAMP '2016-05-15 09:15:00.0', '', TIMESTAMP '2016-05-15 09:00:00.0', STRINGDECODE('Begr\u00fc\u00dfung'), STRINGDECODE('Begr\u00fc\u00dfung')),
(2, STRINGDECODE('<p>Objektive Daten, Moderne Prognoseverfahren, Optimierung von Entscheidungen unter Unsicherheit und Automatisierung sind die S\u00e4ulen, auf denen innovative Unternehmen im Zeitalter der Digitalisierung ihren Erfolg gr\u00fcnden. Im Vortrag werden Ziele, Prinzip, Architektur und Erfolge der Blue Yonder-L\u00f6sungen am Beispiel von verschiedenen Handelsapplikationen beleuchtet, die ein vollst\u00e4ndiges Outsourcing von mathematischer, technischer und operationeller Komplexit\u00e4t erlauben, ohne auf den vollen \u00dcberblick und die volle Kontrolle verzichten zu m\u00fcssen.</p>\n'), TIMESTAMP '2016-05-15 10:15:00.0', '', TIMESTAMP '2016-05-15 09:15:00.0', 'Next generation retail: Die Kreuzung von Machine Learning und Business Software', 'Keynote'),
(3, '', TIMESTAMP '2016-05-15 10:45:00.0', '', TIMESTAMP '2016-05-15 10:15:00.0', 'Kaffeepause', 'Pause'),
  (4, STRINGDECODE(
      '<p>Microservices als Architekturmuster kursieren seit ca. 2 Jahren - zumindest in der breiten Wahrnehnmung - in der Softwarebranche. Eingesetzt bei Unternehmen wie Netflix, dienen Sie dazu, komplexe Anwendungssoftware aus kleinen, unabh\u00e4ngigen Prozessen zu komponieren.<br />\nDie dadurch entstehenden Vorteile haben nat\u00fcrlich auch bei Microservices ihren Preis und die Umsetzung wirft viele Fragen auf. Dabei reicht das Spektrum der Fragen zu Microservices vom \"Warum\" \u00fcber \"die richtige Gr\u00f6\u00dfe\" bis hin zum konkreten \"wie?\".<br />\nDer Vortrag zeigt, wie Microservices auf Basis von ASP.NET Core 1.0 (Cross-Plattform .NET) und dem Docker \u00d6kosystem in der Praxis umgesetzt und sowohl on premise als auch in der Cloud ausgerollt werden k\u00f6nnen.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0',
   STRINGDECODE('Microservices mit ASP.NET Core 1.0 und dem Docker \u00d6kosystem'), 'DNUG KA / Softwerkskammer'),
  (5, STRINGDECODE(
      '<p>Die Forschungsgruppe Cooperation & Management (C&M) und das Fraunhofer Institut f\u00fcr Optronik, Systemtechnik und Bildauswertung (IOSB) arbeiten an der mobilen Web-Anwendung SmartCampus, die Mitgliedern einer Hochschule fortschrittliche Services zur Orientierung auf dem Campus anbietet. Beispiele f\u00fcr solche Services sind die Navigation oder die Unterst\u00fctzung von Lerngruppen auf dem Campus.<br />\nIm Rahmen dieses Beitrags m\u00f6chten wir unsere Vorgehensweise, Erfahrungen und aufgestellten Richtlinien bei der Entwicklung von SmartCampus-Services im Rahmen einer serviceorientierten Architektur (SOA) vorstellen. Nach einer allgemeinen Einf\u00fchrung in die grundlegenden Begrifflichkeiten und den SmartCampus fokussieren wir das API-Design-First-Prinzip und die sich daraus ergebenden Vorteile insbesondere bei verteilten und autonomen Teams. Anschlie\u00dfend stellen wir exemplarisch unsere aufgestellten Richtlinien vor, um eine einheitliche, wohlgeformte sowie qualit\u00e4tsgesicherte Service-Schicht f\u00fcr den SmartCampus sicherzustellen. Diese Richtlinien entstanden in Kooperation mit einem der f\u00fchrenden Anbieter im E-Commerce-Umfeld. Illustriert wird das Vorgehen mithilfe eines Werkzeugs zur API-Spezifikation sowie Code-Beispielen (HTTP, JSON) aus der Kommunikation zwischen Service-Konsumenten und Services. Dar\u00fcber hinaus geben wir einen Ausblick auf weitere Arbeiten im Umfeld des SmartCampus, die im Rahmen der Kooperation der Forschungsgruppe C&M mit dem Fraunhofer IOSB geplant sind.<br />\nF\u00fcr den Beitrag sind keine Voraussetzungen seitens der Teilnehmer erforderlich. Die notwendigen Begrifflichkeiten und Grundlagen werden zu Beginn des Vortrags vermittelt.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0',
   'Entwicklung von ressourcenorientierten Services mithilfe des API-Design-First-Prinzips', 'VKSI - Invited Talks'),
  (6, STRINGDECODE(
      '<p>AngularJS z\u00e4hlt ist das popul\u00e4rste Framework f\u00fcr Single-Page-Anwendungen. Mit Angular 2 mischt Google die Webentwicklung geh\u00f6rig auf. Das neue Framework macht keine Kompromisse. Es setzt auf die Programmiersprache TypeScript, komponentenbasierte Entwicklung, eine neue Template-Syntax und ein verbessertes Tooling. Kurzum: alles ist neu!<br />\nJohannes Hoppe stellt Ihnen Angular 2 mittels einer Live-Coding Session vor. Zum Ende der des Vortrags kennen Sie die wichtigsten Bausteine von Angular 2. Sie werden in der Lage sein, eine erste Anwendung selbst aufzusetzen.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0', 'Live-Coding mit Angular 2', 'JavaScript');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (7, STRINGDECODE(
      '<p>DevOps als ganzheitliche Strategie geht von drei Phasen aus: Build - Measure - Learn. Die Build Phase mit der zeitnahen schnellen Realisierung, Integration und Deployment von neuen Features mit hohem Kundennutzen, haben Teams dank agiler Vorgehensmodelle mittlerweile gut im Griff. Str\u00e4flich vernachl\u00e4ssigt werden aber noch die essenziellen Phasen Messen und Verbessern. Viele Teams haben hier noch keine konkrete Idee, wie ein pragmatischer Messprozess aussehen kann. Der Vortrag wird sich deshalb dem Thema \"Measure und Learn\" aus Service- und Nutzerperspektive annehmen. Leitfragen sind bspw.: Arbeitet meine Infrastruktur noch wie geplant? Werden Dienste unbemerkt langsamer? Welche Funktionalit\u00e4ten werden genutzt? Funktionieren Apps und Services \u00fcberall wie geplant? Abgerundert werden die Fragen mit Demos aus AIT Projekten, bei denen u.a. das VS Last & Performance Test Framework sowie der Cloud-Dienst Application Insights neu zu einem 360\u00b0 Applikationsmonitoring zusammengestellt wurden.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0', 'Monitoring 2.0: Alles im Lot?',
   'DevOps / Anwendungssicherheit'),
  (8, STRINGDECODE(
      '<p>Viele Organisationen haben in den letzten Jahren eine Plattform f\u00fcr Continuous Integration oder gar Continuous Delivery aufgebaut. Man kann auch die Plattform selbst nach dem Prinzip \"Infrastructure as Code\" erstellen und kontinuierlich reproduzieren und testen, sie also zum Gegenstand einer Continuous Delivery Strategie machen. Damit l\u00e4sst sich nicht nur die Funktionsf\u00e4higkeit der Plattform permanent pr\u00fcfen und garantieren, sondern es k\u00f6nnen auch sehr leicht weitergehende Use Cases abgebildet werden, z.B. Weiterentwicklung/Upgrade einer Komponente, Integration neuer Komponenten/Plugins, Austausch einer Komponente. Au\u00dferdem lassen sich beispielsweise Mitarbeiter oder Entwickler damit schulen oder komplexe CI-/CD-Prozesse testen, beispielsweise das Release von Komponenten. Im Vortrag wird eine Referenz-Plattform aus typischen Open Source-Komponenten, wie sie sich im Java-/Enterprise-Umfeld finden, vorgestellt:<br />\n- SCM-Manager (Source Code Management mit SVN, Git oder Mercurial)<br />\n- Nexus (Artifact-Repository)<br />\n- Jenkins (Build-Server)<br />\nEs wird zun\u00e4chst demonstriert und erkl\u00e4rt, wie die die Plattform als virtuelle Maschine mit Vagrant und Puppet aufgebaut wird, die Komponenten werden dabei weitgehend als Docker-Container installiert. Anschlie\u00dfend wird gezeigt, wie die Plattform sich selbst reproduziert und dabei kontinuierlich einen Test-Prozess durchl\u00e4uft. Zum Testen wird ein Java-basiertes Software-Paket als Referenz-Projekt von der automatisch errichteten Plattform gebaut. Ein Reproduktions-Zyklus ist abgeschlossen, wenn die Plattform das Referenz-Projekt erfolgreich bauen kann. Typische Ereignisse, die einen erneuten Durchlauf erfordern und ansto\u00dfen, werden diskutiert, u.a.<br />\n- \u00c4nderungen an den bestehenden Komponenten, z.B.,<br />\n- Einf\u00fcgen neuer Jenkins-Plugins<br />\n- Update auf neue Versionen der Komponenten<br />\n- \u00c4nderungen am Build-Prozess des Referenz-Projekts.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0',
   'Continuous Delivery of Continuous Delivery', 'Java User Group'),
  (9, STRINGDECODE(
      '<p>Die Idee hinter Microservices ist einfache, wiederverwendbare und entkoppelte Services zu schaffen, die sich leicht kombinieren lassen um ein spezifische Funktionalit\u00e4t zu implementieren. Diese Services m\u00fcssen auch einfach zu starten, stoppen, skalieren und zu betreiben sein. Solche Applikationen mit Java Enterprise, oder mit \u00e4hnlich komplexe Technologien zu implementieren ist wie mit Kanonen auf Spatzen zu schie\u00dfen. Es kann vielleicht klappen, aber zu welchem Kosten?<br />\nIn diesem Vortrag implementieren wir eine kleine REST-basierte Applikation von Grund auf mit zwei leichtgewichtige Frameworks: JayWire f\u00fcr Dependency Injection, und das Spark Framework als Web Framework. Das Endresultat wird einen Ausf\u00fchrbaren Archiv generieren die keinen Container braucht zum Laufen, und das alles ganz ohne Annotations, Classpath Scanning, Reflection oder andere Tricksereien.</p>\n'),
   TIMESTAMP '2016-05-15 11:30:00.0', '', TIMESTAMP '2016-05-15 10:45:00.0',
   'Leichtgewichtige Microservices mit JayWire und das Spark Framework', 'Microservices / Tools');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (10, STRINGDECODE(
      '<p>Bei der UI-Testautomatisierung lauern in der Praxis viele T\u00fccken. Diese T\u00fccken sorgen gerne daf\u00fcr das Entwickler Tage und N\u00e4chste investieren m\u00fcssen um Tests umzusetzen. Lernen sie anhand von Beispielen aus unserem Projektalltag wie sie typische Probleme bei der UI Testautomatisierung mit CodedUI fr\u00fchzeitig verhindern k\u00f6nnen. Das Motto lautet hier: \"Vorbereitung ist die halbe Miete\". Aus diesen Motto leiten sich die wesentliche Inhalte f\u00fcr den Vortrag ab:<br />\nWie sind Anwendungen (WPF, WinForms, Universal Apps) f\u00fcr UI-Tests zu optimieren?<br />\nWie sieht ein wartbarer CodedUI-Test aus?<br />\nWie sieht eine CodedUI Codebasis f\u00fcr gro\u00dfe Anwendungen aus?<br />\nWas sind die typischen Do''s und Dont''s?<br />\nUnd zu guter Letzt: Wann verwende ich besser kein CodedUI?</p>\n'),
   TIMESTAMP '2016-05-15 12:30:00.0', '', TIMESTAMP '2016-05-15 11:45:00.0',
   'CodedUI - Richtig vorbereitet ist halb getestet!', 'DNUG KA / Softwerkskammer'),
  (11, '', TIMESTAMP '2016-05-15 12:30:00.0', '', TIMESTAMP '2016-05-15 11:45:00.0', 'Pecha Kuchas',
   'VKSI - Invited Talks'),
  (12, STRINGDECODE(
      '<p>React.js ist eine relativ neue, von Facebook entwickelte Library, die auf einem radikal anderen Ansatz beruht als andere, da sie selbst entscheidet, welche Teile der Webseite bei Daten\u00e4nderungen neu gerendert werden m\u00fcssen. Dadurch ist viel Last vom Entwickler genommen, der sich nun nicht mehr darum k\u00fcmmern muss, seine Daten und die zugeh\u00f6rigen Anzeigen synchron zu halten.<br />\nDiese neue Freiheit ist die Grundvoraussetzung daf\u00fcr, dass auch die Behandlung der Daten selbst im Frontend in einer einfacheren Weise stattfinden kann. Das zu React.js passende, ebenfalls von Facebook entwickelte Pattern hei\u00dft Flux, und eine besonders elegante Implementierung namens Redux ist derzeit in aller Munde. Hierbei werden s\u00e4mtliche Frontend-Daten in einem einzigen strukturierten Datenobjekt gespeichert, aus dem die React-Komponenten beim Rendern ihre Werte beziehen. React stellt hierbei sicher, dass nichts unn\u00f6tig gerendert wird.<br />\nDiese Kombination vereinfacht die Frontend-Entwicklung ungemein, da man nicht mehr auf event-getriebene Reaktionen auf Wert\u00e4nderungen angewiesen ist, sondern die Weboberfl\u00e4che in einem deklarativen Stil erstellen kann. Der Vortrag stellt anhand eines kleinen Beispiels vor, wie sich der Einsatz von React und Redux anf\u00fchlt und welche Vorgehensweisen sich bei der Entwicklung etabliert haben.</p>\n'),
   TIMESTAMP '2016-05-15 12:30:00.0', '', TIMESTAMP '2016-05-15 11:45:00.0', 'Schnelle Reaktion - mit React und Redux',
   'JavaScript'),
  (13, STRINGDECODE(
      '<p>Im Zeitalter von Microservices und Applikationen, die sich aus vielen verteilten Komponenten zusammensetzen und CSRF und CORS Aspekte abdecken m\u00fcssen sind Session-Cookies nicht mehr praktikabel.<br />\nToken versprechen die heilvolle Alternative zu sein und mit JWT (JSON Web Token) liegt der Entwurf f\u00fcr einen Standard vor, der die Verwendung von Tokens innerhalb einer Applikationslandschaft vereinfachen soll.<br />\nDer Talk zeigt, ob Tokens ihr versprechen halten und Webapplikationen auch ohne Kekse ein Genuss bleiben.</p>\n'),
   TIMESTAMP '2016-05-15 12:30:00.0', '', TIMESTAMP '2016-05-15 11:45:00.0', 'Token statt Cookies',
   'Microservices / Tools'),
  (14, STRINGDECODE(
      '<p>Wir sind Entwickler in einem dediziertem Team f\u00fcr Release- und Testautomatisierung, das eine Continuous Delivery Pipeline f\u00fcr eine f\u00fchrende eCommerce-Plattform (mit aktuell ca. 170.000 Shops) betreibt.<br />\nIm letzten Jahr hat sich aufgrund der Vielzahl der Pipeline-Maschinen (f\u00fcr die jeweiligen Installations- und Patchtypen) der Zeitaufwand f\u00fcr die Auswertung von Testergebnissen (Selenium) deutlich erh\u00f6ht. Als Automatisierungsspezialisten haben wir uns deshalb f\u00fcr die Einf\u00fchrung einer zentralen Datenbank f\u00fcr alle Testergebnisse, die bei jedem Pipeline-Durchlauf zu Hunderten generiert werden, entschieden.<br />\nUnsere Implementierung setzt hierbei auf den REST-basierten ELK-Stack (Elasticsearch, Logstash, Kibana), welcher sich beim performantem und verteiltem Monitoring von System-Logs etabliert hat. Die technologische Basis haben wir hierbei um die M\u00f6glichkeit Test-Reports mit verschiedenen Administrations- und UI-Clients zu verwalten erweitert. Zudem wurden die einzelnen Bestandteile durch verschiedene Docker-Container mit Template-basierten Konfigurationsdateien (Jinja2) sowie Provisioning-Tools (Ansible, Puppet) entkoppelt.<br />\nKonsequent haben wir dabei auf eine testgetriebene Entwicklung mittels CircleCI gesetzt. Die fertige Gesamtl\u00f6sung wird unter Einsatz einer eigenen Docker-Registry bedarfsgerecht in den einzelnen, korrespondierenden Jenkins-Jobs eingebunden. Dabei sorgen die Logstash-Container daf\u00fcr die individuellen Testergebnisse mit Umgebungsdaten anzureichen.<br />\nIn unserer Session pr\u00e4sentieren wir Euch unsere Motivation, m\u00f6gliche L\u00f6sungsans\u00e4tze und die implementierten L\u00f6sungsbestandteile, insbesondere im praxisnahem Zusammenspiel innerhalb einer Continuous Delivery Pipeline.<br />\nLasst Euch von der Vielfalt an Einsatzm\u00f6glichkeiten innerhalb eines produktiven Kontexts \u00fcberraschen!</p>\n'),
   TIMESTAMP '2016-05-15 12:30:00.0', '', TIMESTAMP '2016-05-15 11:45:00.0', STRINGDECODE(
       'Ein(Blick) in die Maschinerie einer Continuous Delivery Pipeline. Automatisierte Testaggregation und -auswertung f\u00fcr angehende DevOps.'),
   'DevOps / Anwendungssicherheit');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(15, '', TIMESTAMP '2016-05-15 13:30:00.0', '', TIMESTAMP '2016-05-15 12:30:00.0', 'Mittagspause', 'Pause'),
  (16, STRINGDECODE(
      '<p>In this session I give an interactive explanation of the Git Object Database. We will explore the different kind of Git objects, how they are stored and the plumbing commands to interact with them. All this will be done live and visualized by gitviz (<a href=\"https://www.npmjs.com/package/gitviz\">https://www.npmjs.com/package/gitviz</a>), for easier understanding.<br />\nAfter this session you will be able to tell why xkcd 1597 about \"Git\" (<a href=\"https://xkcd.com/1597/\">https://xkcd.com/1597/</a>) is so completely wrong.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0', 'A deep dive into the Git internals',
   'DNUG KA / Softwerkskammer'),
  (17, STRINGDECODE(
      '<p>Duplizierter Code ist immer \u00e4rgerlich, vor allem, wenn er im Anschluss leicht modifiziert wurde. Bei hohem Zeitdruck werden teilweise ganze Produkte kopiert um kundenspezifische Anpassungen vorzunehmen. Schon bald gr\u00fc\u00dft die Wartungsh\u00f6lle. Dieser Vortrag zeigt die Probleme, mit denen sich Entwicklungsteams beim Refaktorisieren solcher Software-Kopien konfrontiert sehen und wie man sie zumindest teilweise l\u00f6sen kann.<br />\nAls L\u00f6sungsm\u00f6glichkeit werden Softwareproduktlinien (SPL) vorgestellt, bei denen eine Kernfunktionalit\u00e4t mit verschiedenen Funktionsbausteinen zu fertigen Produkten kombiniert werden k\u00f6nnen. Der Weg zum Ziel ist die konsistente Refaktorisierung der Kopien in eine SPL. Bei komplexen Produktkopien erfordert das jedoch Personenjahre. In diesem Vortrag werden die notwendigen f\u00fcnf Refaktorisierungsschritte vorgestellt und gezeigt, wie man durch sinnvollen Einsatz von Werkzeugen den notwendigen Aufwand deutlich reduzieren kann. Dabei wird insbesondere auch auf die variable Einbindung von Funktionalit\u00e4t auf Quelltextebene eingegangen, die ebenfalls au\u00dferhalb des SPL-Kontexts eingesetzt werden kann.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0',
   STRINGDECODE('Softwareproduktlinien: Ain\u2019t Nobody Got Time For That?'), 'VKSI - Invited Talks'),
  (18, STRINGDECODE(
      '<p>JavaScript ist seit ein paar Jahren die neue Hypesprache. Sie hat eine aktive Community und zahlreiche Frameworks, mit jedem Einsatzgebiet das man sich vorstellen kann. Sp\u00e4testens seit dem Sprung ins Backend mit Node.js ist sie eine der popul\u00e4rsten Sprachen geworden. Leider wurde JavaScript nicht f\u00fcr diesen umfassenden Einsatz entworfen. Das macht sich in fehlenden Sprachfeatures bemerkbar, wie zum Beispiel lokalem Scoping oder einem standardisierten Modulkonzept. Mit ECMAScript 6 wurden viele dieser lang erwarteten Erweiterungen in die Sprache aufgenommen.<br />\nWas JavaScript aus der Sicht von vielen Backendentwicklern noch fehlt ist ein ausgereiftes Typsystem. Genau dort setzt TypeScript an, indem es optionale statische Typisierung mit all seinen Vorteilen bereitstellt. Als echte Obermenge von JavaScript kompiliert es zur\u00fcck zu ECMAScript 5 oder 6 und garantiert damit volle Abw\u00e4rtskompatibilit\u00e4t. Au\u00dferdem strebt es eine volle Angleichung an die JavaScript-Spezifikation an, womit es das JavaScript der Zukunft werden soll. Der Vortrag stellt die verschiedenen M\u00f6glichkeiten des Typsystems von TypeScript vor und zeigt auf, welche praktischen Vorteile sich dadurch ergeben. Diese beruhen hautps\u00e4chlich auf der Erfahrung in komplexen Projekten, die mit TypeScript geb\u00e4ndigt werden konnten.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0',
   'Typen nach JavaScript tragen - TypeScript', 'JavaScript'),
  (19, STRINGDECODE(
      '<p>Es ist eine sch\u00f6ne Illusion: M\u00fchsam hat man sich eine ganze Suite an automatisierten Unit-, Komponenten- und Integrationstests aufgebaut. Man hat sich ein Sicherheitsnetz aufgebaut. Das gibt ein gutes Gef\u00fchl. Zumindest so lange alle Tests \u201egr\u00fcn\u201c sind. Leider holt uns die Realit\u00e4t h\u00e4ufg bald auf verschiedene Arten wieder ein:<br />\n- Ein Test wird rot und niemand wei\u00df warum. Selbst bei intensiver Betrachtung ist noch nicht einmal klar, was der Test eigentlich sicherstellen sollte.<br />\n- Ein kleines Refactoring macht auf einen Schlag mehrere hundert Tests unbrauchbar<br />\n- Tests schlagen nicht an, obwohl die Funktionalit\u00e4t, die sie absichern sollten bricht<br />\n- Tests bestehen aus Mocks, die Mocks aufrufen, die Mocks konfigurieren und am Ende steht eine Assertion, die eigentlich nur pr\u00fcft, ob wir unseren Mock richtig konfiguriert haben<br />\n- Usw.<br />\nOft steht man dann vor der Entscheidung: Intensiv forschen, Test wegwerfen, oder hoffen, dass er sich von selbst wieder l\u00f6st. Dabei haben wir es doch nur gut gemeint, als wir die Tests geschrieben haben.<br />\nBei unserer t\u00e4glichen Arbeit in Softwareprojekten und bei unserer T\u00e4tigkeit als Coaches f\u00fcr Agiles Software Engineering haben wir immer wieder schmerzhaft erfahren, dass \u201egut gemeint\u201c das Gegenteil von \u201egut\u201c ist. Dass man sich im Sicherheitsnetz schmerzhaft verfangen kann und das Netz zerschneiden muss. Wir haben unsere Schl\u00fcsse daraus gezogen.<br />\nIm Vortrag wollen wir die unserer Meinung nach wesentlichsten Antipatterns f\u00fcr automatisierte Tests vorstellen. Au\u00dferdem zeigen wir Wege auf, wie man in den meisten F\u00e4llen mit ganz einfachen Mitteln stabile, aussagekr\u00e4ftige Tests schreiben kann, die nachhaltig unsere Produkte sch\u00fctzen.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0',
   STRINGDECODE('Die 10 goldenen Regeln f\u00fcr schlechte Tests'), 'Java User Group');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (20, STRINGDECODE(
      '<p>Test-Driven-Development hat sich mittlerweile in der Java-Welt fest etabliert. Nicht selten wir 50% der Entwicklungszeit mit der Erstellung und Wartung von automatisierten Tests verbracht. Leider werden normale Tests oft nicht in der Fachsprache der Anwendung geschrieben und k\u00f6nnen nur von Programmieren gelesen werden.<br />\nBehavior-Driven Development (BDD) schafft hier Abhilfe indem Tests in Form von ausf\u00fchrbaren Szenarien geschrieben werden. Szenarien k\u00f6nnen von Fachexperten ohne Programmierkenntnisse gelesen und bewertet werden und bilden dadurch eine automatisch validierte Verhaltensdokumentation der Anwendung.<br />\nJGiven ist ein neues Open-Source BDD-Framework in dem Szenarien in einer fluent Java-API geschrieben werden. JGiven-Szenarien werden in der g\u00e4ngigen Given-When-Then-Form geschrieben. Durch ein modulares Konzept lassen sich neue Szenarien leicht aus Teilen anderer Szenarien zusammensetzen. Dies beschleunigt die Erstellung von neuen Szenarien und vermeidet Testcode-Duplizierung. Da weder Groovy noch Scala ben\u00f6tigt werden und JGiven kompatibel zu JUnit und TestNG ist, kann JGiven sofort in Java-Projekten eingesetzt werden und leicht in bestehende Test-Infrastrukturen integriert werden. JGiven erzeugt au\u00dferdem einen interaktiven HTML5-Report, der es Fachexperten erm\u00f6glicht die von Entwicklern geschriebenen Szenarien zu lesen und zu beurteilen.<br />\nJGiven wird seit \u00fcber einem Jahr aktiv in einem gro\u00dfen Java-Projekt eingesetzt. Mitterweile wurden schon \u00fcber 2000 Szenarien geschrieben. Der Test-Code ist dadurch erheblich lesbarer und wiederverwendbarer geworden und der erzeugte Report stellt einen erheblich Mehrwert f\u00fcr unsere Fachexperten als auch die Entwickler selbst dar.<br />\nIn dem Vortrag wird der Autor eine Einf\u00fchrung in JGiven geben und anhand einer kurzen Live-Coding-Session zeigen wie schnell und einfach BDD-Szenarien in JGiven geschrieben werden k\u00f6nnen.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0',
   'Behavior-Driven Development in Java mit JGiven', 'Microservices / Tools'),
  (21, STRINGDECODE(
      '<p>Allzu oft steht bei der Entwicklung von Software allein die Funktionalit\u00e4t im Vordergrund. So kann es passieren, dass erst in finalen Abnahmeprozessen der Datenschutz Beachtung findet. Oft wird Datenschutz dabei zum Showstopper - aufw\u00e4ndige nachtr\u00e4gliche Anpassungen sind die Folge, die Zeit, Nerven und Geld kosten.<br />\nDaher ist es umso sinnvoller, etwaige Datenschutzprobleme schon bei der Entwicklung von Software zu erkennen und den Datenschutz von vorneherein in die Gesamtkonzeption einzubauen. Dieser Ansatz wird als Privacy by Design (PbD) bezeichnet.<br />\nBislang war PbD eher eine Wunschvorstellung der Datensch\u00fctzer. Mit der voraussichtlich in 2018 in Kraft tretenden EU-Datenschutzgrundverordnung wird PbD zu einer Grundanforderung des Datenschutzrechts. H\u00f6chste Zeit f\u00fcr Entwickler, sich etwas genauer mit diesem Ansatz zu besch\u00e4ftigen.</p>\n'),
   TIMESTAMP '2016-05-15 14:15:00.0', '', TIMESTAMP '2016-05-15 13:30:00.0',
   STRINGDECODE('Privacy by Design: Wunschvorstellung oder Qualit\u00e4tsmerkmal?'), 'DevOps / Anwendungssicherheit'),
  (22, STRINGDECODE(
      '<p>Durch die stetig zunehmenden Sicherheitsanforderungen an Software ist ein sicherer Entwicklungsprozess unabdingbar. Dieser beinhaltet sicherheitsrelevante Prozesse wie die Durchf\u00fchrung von Bedrohungsanalysen, sichere Implementierung und Security-Testing.<br />\nBereits in der Designphase sollten strukturierte Bedrohungsanalysen durchgef\u00fchrt werden. So k\u00f6nnen Bedrohungen fr\u00fchzeitig identifiziert, Sicherheitsma\u00dfnahmen in das Design integriert und damit Sicherheitsprobleme proaktiv vorgebeugt werden.<br />\nDie Bedrohungsanalyse darf nicht nur der Dokumentation dienen, um Compliance-Anforderungen zu erf\u00fcllen. Vielmehr muss sie verst\u00e4ndlich f\u00fcr alle in der Softwareentwicklung involvierten Stakeholder (bspw. Architekten, Entwickler und Tester) und praktisch durchf\u00fchrbar sein.<br />\nDieser Vortrag stellt anhand eines Beispiels die Durchf\u00fchrung einer Bedrohungsanalyse mit Hilfe des Microsoft STRIDE Tools vor. Die Analyse basiert auf der Architektur der Anwendung. Gro\u00dfe, komplexe Anwendungen werden durch mehrere hierarchisch aufgebaute Architekturdiagramme dargestellt. Der Vortrag zeigt die Rollen und Aufgaben verschiedener Stakeholder in der Durchf\u00fchrung der Bedrohungsanalyse. Es wird deutlich, dass die Architekturdiagramme das gemeinsame Verst\u00e4ndnis \u00fcber die Anwendung und die Kommunikation der Stakeholder \u00fcber (potentielle) Sicherheitsprobleme unterst\u00fctzen.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0',
   STRINGDECODE('Bedrohungsanalyse f\u00fcr die Entwicklung'), 'DevOps / Anwendungssicherheit');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (23, STRINGDECODE(
      '<p>Der Hamburger Hafen hat in vielen Bereichen eine sehr gute Computerunterst\u00fctzung. In anderen noch nicht: Ohne IT-Unterst\u00fctzung lief bisher die Simulation von Schiffsman\u00f6vern (wie das Ein- und Auslaufen im Hamburger Hafen) ab. Um dies zu ver\u00e4ndern, musste die traditionelle \u201eLineal-Stift-und-Papierkarten-L\u00f6sung\u201c durch ein Softwaresystem ersetzt werden. Softwareanwendungen scheiterten bisher daran, dass nur Maus und Tastatur als Eingabewerkzeuge zur Verf\u00fcgung standen. Diese waren f\u00fcr die Aufgabe aus Sicht der Nautiker nur schlecht geeignet und der traditionellen L\u00f6sung unterlegen.<br />\nMulti-Touch-Screens in Tischgr\u00f6\u00dfe bieten heute v\u00f6llig neue Interaktionsm\u00f6glichkeiten, die nun auch die Schiffsman\u00f6versimulation digitalisierbar machen. Durch Gesten l\u00e4sst sich Software so steuern, dass Schiffsman\u00f6ver intuitiv und schnell ausprobiert werden k\u00f6nnen \u2013 auf dem digitalen Peiltisch! Dieser Tisch stellt die Hafenkarte mit den Tiefendaten der Elbe dar, den so genannten Peilplan. Mit den Fingern kann der Peilplan verschoben und vergr\u00f6\u00dfert oder verkleinert werden, \u00e4hnlich wie man es vom Smartphone oder Tablet-Computer kennt.<br />\nDie bisher aus Pappe ausgeschnittenen Schiffsilhouetten werden als Umrisse dargestellt, die sich mit den Fingern \u00fcber die Karte navigieren lassen. Da auch die Form des Unterwasserschiffs und der Tiefgang ber\u00fccksichtigt werden, lassen sich Gefahrensituationen bei Man\u00f6vern interaktiv signalisieren. Die vorher \u201edumme\u201c Karte aus Papier wird also selbst digital und \u201esmart\u201c.<br />\nIn diesem Vortrag zeigen wir, wie die Papierl\u00f6sung durch einen Multi-Touch-Tisch auf Windows-10-Basis mit C#, .NET ersetzt wird. Wir betrachten auch Cross-Plattform-Aspekte; die darunterliegende Server-Infrastruktur ist in Java implementiert und mit REST angebunden.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0',
   'Der Peiltisch - Digitale Revolution im Hafen', 'DNUG KA / Softwerkskammer'),
  (24, STRINGDECODE(
      '<p>Die deutsche Automobilindustrie steht vor gro\u00dfen Herausforderungen durch neue Mobilit\u00e4tskonzepte, digitale Gesch\u00e4ftsmodelle und starke internationale Wettbewerber bei digitalen Mobilit\u00e4tsdienstleistungen (Google, Apple). Zur Unterst\u00fctzung der digitalen Transformation im Bereich Smart Mobility und Smart City f\u00f6rdert der Freistaat Bayern das TUM Living Lab Connected Mobility, ein interdisziplin\u00e4res Forschungsprojekt, das die Kompetenzen der TU M\u00fcnchen in den Bereichen Informatik und Verkehrsforschung b\u00fcndelt. Das Ziel des Projekts ist es, innovative Beitr\u00e4ge zum Design, zur Architektur und zur skalierbaren Realisierung einer offenen herstellerunabh\u00e4ngigen digitalen Mobilit\u00e4tsplattform zu leisten. Die Plattform wird in enger Zusammenarbeit mit f\u00fchrenden Industriepartnern entwickelt und bietet kleinen und mittelst\u00e4ndischen Unternehmen einen Marktplatz, um digitale Mobilit\u00e4tsdienstleistungen mit substanziell geringeren finanziellen, organisatorischen und technischen Aufwand zu entwickeln, zu betreiben und miteinander zu vernetzen.<br />\nWeitere Informationen zum Projekt: <a href=\"http://www.tum-llcm.de\">www.tum-llcm.de</a><br />\nProf. Matthes als Projektleiter gibt einen \u00dcberblick das Projektvorhaben (8 Lehrst\u00fchle, 19 Promotionen), und zeigt M\u00f6glichkeiten der Beteiligung an der offenen Plattform und in ihrem \u00d6kosystem f\u00fcr die Teilnehmer des Karlsruher Entwicklungstags auf.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0', 'TUM Living Lab Connected Mobility',
   'VKSI - Invited Talks'),
  (25, STRINGDECODE(
      '<p>Bei der sogenannten reaktiven Programmierung handelt es sich um ein Programmierparadigma, dass sich an Datenstr\u00f6men orientiert und eine saubere und robustere Entwicklung von asynchronen Abl\u00e4ufen erm\u00f6glicht. Neben einer grundlegenden Einf\u00fchrung in die Konzepte der reaktiven Programmierung soll ein Einblick in die Bibliothek  ReactiveX bzw. dessen Erweiterung f\u00fcr JavaScript gegeben werden. Diese Bibliothek vereint Konzepte des Observer- und Iterator-Pattern als auch der funktionalen Programmierung. Durch eine Gegen\u00fcberstellung mit einer eher weitverbreiteten Methodik f\u00fcr asynchrone Abl\u00e4ufe aus dem Bereich der Web-Entwicklung (Stichwort: Callbacks) soll anschlie\u00dfend der Kontrast geschaffen und damit die Vorz\u00fcge der reaktiven Programmierung aufgezeigt werden. Die aufgezeigten Konzepte stellen das Fundament von zahlreichen hochfrequentierten Web-Anwendungen dar, wie bspw. Netflix oder Zalando.<br />\nF\u00fcr den Beitrag werden keine Voraussetzungen seitens der Teilnehmer vonn\u00f6ten sein. Die notwendigen Begrifflichkeiten, Techniken und Grundlagen werden w\u00e4hrend des Vortrags vermittelt. Lediglich ein grundlegendes Verst\u00e4ndnis von JavaScript ist empfehlenswert, um die Quellcode-Beispiele besser verstehen zu k\u00f6nnen. Das grundlegende Konzept ist aber unabh\u00e4ngig von einer konkreten Programmiersprache anwendbar.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0',
   'Reactive Programming - Asynchronous Processing done right', 'JavaScript');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (26, STRINGDECODE(
      '<p>Das Jahr hat 365 Tage, der Tag 24 Stunden, jede Minute 60 Sekunden, eine Sekunde folgt auf die n\u00e4chste - und jede der vorigen Aussagen ist falsch. Uhrzeit und Timestamp, Zeitspannen und Zeitzonen - ein Ausflug in ein Thema, das auf den ersten Blick so nebens\u00e4chlich erscheint und in der Praxis \u00fcberraschende Schmerzen verursachen kann. Der ein oder andere \u00fcberraschte Klatsch auf die Stirn d\u00fcrfte garantiert sein.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0',
   'Wieviel Uhr ist es? Pitfalls mit Datum und Uhrzeit', 'Java User Group'),
  (27, STRINGDECODE(
      '<p>Beim Test-Framework JUnit hat im Oktober letzten Jahres die Entwicklung der grundlegend \u00fcberarbeiteten Version 5 begonnen. Nach einer Startfinanzierung durch die Crowdfunding-Kampagne \"JUnit Lambda\" und einem Alpha-Release im Februar arbeitet das JUnit5-Team mittlerweile an Milestone-Releases, die noch in diesem Jahr zu einer Version 5.0 f\u00fchren sollen.<br />\nWir berichten in diesem Vortrag \u00fcber die Gr\u00fcnde f\u00fcr die Neuentwicklung, sowie \u00fcber die Vision f\u00fcr JUnit5. Weiter stellen wir die neue, stark modulare Architektur vor, die mit besonderem Fokus auf Erweiterbarkeit und Integrierbarkeit in Tools entworfen wurde. Nicht zuletzt bringt das neue Release neben vielem Bew\u00e4hrten auch manche interessante Neuerungen f\u00fcr Entwickler, die in diesem Vortrag nat\u00fcrlich nicht zu kurz kommen sollen.</p>\n'),
   TIMESTAMP '2016-05-15 15:15:00.0', '', TIMESTAMP '2016-05-15 14:30:00.0', 'JUnit5 - Was bringt die neue Version?',
   'Microservices / Tools'),
(28, '', TIMESTAMP '2016-05-15 15:45:00.0', '', TIMESTAMP '2016-05-15 15:15:00.0', 'Kaffeepause', 'Pause'),
  (29, STRINGDECODE(
      '<p>Sicherheit in Web-Anwendungen ist ein alter Hut. Dazu hat jeder Entwickler schon genug Artikel gelesen und Vortr\u00e4ge geh\u00f6rt. K\u00f6nnte man meinen. Doch die Realit\u00e4t zeigt uns ein anderes Bild. Fast t\u00e4glich tauchen neue Meldungen \u00fcber gehackte Webseiten auf. Wir k\u00f6nnen sicher sein, dass hier auf Seiten der gehackten nicht nur Anf\u00e4nger Fehler gemacht haben, sondern auch erfahrenen Entwicklern Fl\u00fcchtigkeitsfehler passiert sind. Wie lassen sich solche Fehler auch ohne ein teures Audit von Spezialisten finden? Wie kann man als Entwickler ein Gesp\u00fcr f\u00fcr Sicherheitskritische Stellen im Code entwickeln? In diesem Tutorial wird anhand einer Demo-Anwendung gezeigt, wie man die eigene Anwendung einem Security-Check unterziehen kann und wie man sich so mit Hilfe von frei verf\u00fcgbarem Material langsam an die Denkweise von Hackern herantasten kann.</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0',
   STRINGDECODE('Don''t be the next AshleyMadison: Penetrationstests f\u00fcr Entwickler'),
   'DevOps / Anwendungssicherheit'),
  (30, STRINGDECODE(
      '<p>Auch wenn viele Entwickler angeben, dass sie ihr Hobby zum Beruf gemacht haben sieht die Welt in der Praxis oftmals weniger rosig aus. Aus den verschiedensten Gr\u00fcnden sind wir als Entwickler gefrustet und denken uns \"so hatte ich mir das aber nicht vorgestellt\". Nicht selten liegt es an Erwartungshaltungen, die in der Realit\u00e4t so nicht getroffen werden. Aber auch externe Faktoren bringen uns dazu hier und da die Freude an der Arbeit aus den Augen zu verlieren.<br />\nDoch warum ist das so und vor allem: Was k\u00f6nnen wir direkt und unmittelbar tun um unsere Situation zu verbessern? Anhand konkreter Denkanst\u00f6\u00dfe und Ideen werden Beispiele aufgezeigt, die sofort im realen Leben angewendet werden k\u00f6nnen - analog zu Software Design Patterns werden einige \"Happiness Patterns\" vorgestellt, die dabei helfen k\u00f6nnen den ganz normalen Alltagswahnsinn ein wenig leichter zu \u00fcberstehen.</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0',
   'Developer Karma: How to be a happy developer. Now!', 'DNUG KA / Softwerkskammer'),
  (31, STRINGDECODE(
      '<p>Type-Annotations nach JSR-308 sind Bestandteil von Java 8. Es scheint aber, dass dieses Feature in der Praxis selten Verwendung findet, \u00e4hnlich wie vor 12 Jahren bei der Einf\u00fchrung von Java-Generics. Analog zu den Typ-Parametern wird als prim\u00e4res Feature von JSR-308 die Minimierung von Bugs angepriesen. Daher stellt dieser Vortrag JSR-308 im Detail vor und zeigt, wie Bugs vermieden werden und wie mit dessen Hilfe die Lesbarkeit und die Dokumentation von Code verbessert werden kann. An einem Beispielprojekt wird Live demonstriert wie mit wenig Aufwand Type-Annotations eingef\u00fchrt werden und infolge dessen die Source-Code-Qualit\u00e4t durch Fehlervermeidung wesentlich verbessert wird.</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0', 'Besseren Java-Code mit Type Annotations',
   'Microservices / Tools');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (32, STRINGDECODE(
      '<p>Mit Apache Cordova (ehem. PhoneGap) gibt es seit 2009 die M\u00f6glichkeit mobile Anwendungen mit JavaScript zu schreiben, die User Experience (UX) ist jedoch nicht wie vom Anwender erwartet. Mit Facebooks \u201cReact Native\u201d und Teleriks \u201cNativScript\u201d gibt es seit 2015 einen neuen Ansatz native Apps mit JavaScript zu entwickeln.<br />\nDer Vortrag soll einen \u00dcberblick \u00fcber die aktuellen M\u00f6glichkeiten geben und bei folgenden Fragen weiterhelfen:<br />\nWelche Vorraussetzungen gibt es f\u00fcr mein Projekt?<br />\nWie kann ich eine bestehende Codebasis weiterverwenden?<br />\nWelches Vorgehen bietet sich bei einer Neuentwicklung an?<br />\nWelche Vor-/ Nachteile gibt es f\u00fcr den Kunden?</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0',
   STRINGDECODE('JS goes mobile: Eine \u00dcbersicht'), 'JavaScript'),
  (33, STRINGDECODE(
      '<p>Der Vortrag von Prof. Zollner gibt einen kurzen \u00dcberblick \u00fcber den aktuellen Stand, die Roadmap und die aktuellen Prognosen im Bereich vernetzter und automatisierter/autonomer Fahrzeuge. Studien zeigen, dass die \u201etechnische Revolution\u201c begonnen hat und vielf\u00e4ltige Auswirkungen auf die zuk\u00fcnftige Mobilit\u00e4t hat. Der Vortrag geht auf die zuk\u00fcnftigen, speziell die technologischen Handlungsfelder und die Chancen durch die Digitalisierung ein und gibt einen kurzen \u00dcberblick \u00fcber die aktuellen Initiativen des FZI und des Landes Baden-W\u00fcrttemberg in diesem Kontext ein.</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0',
   'Vernetztes und Autonomes Fahren - Chancen und Herausforderungen', 'VKSI - Invited Talks'),
  (34, STRINGDECODE(
      '<p>In einem aktuellen Projekt haben wir uns entschieden die Lastests nicht mit den \"klassischen\" Tools wie JMeter oder Gattling umzusetzen, sondern unsere Applikation mit massiv parallelen Selenium-Webtests unter Last zu setzen. Hauptargumente daf\u00fcr waren:<br />\n* Schwieriges Setup einer realistischen Lastsimulation bei stark AJAX-lastigen Webseiten mit den klassischen Tools.<br />\n* Wiederverwendung der Webtests und deren Infrastruktur und Page Objects in den Lastest.<br />\n* Vertrauter Java Code in den Tests (keine Einarbeitung in neue Tools n\u00f6tig).<br />\n* Messung der Lade- und Interaktionszeiten w\u00e4hrend der Lastests in echten Browsers.<br />\nKern dieses Konzepts war ein neuer JUnit-Runner mit welchem wir beliebige Tests n-fach parallel und m-fach nacheinander ausf\u00fchren k\u00f6nnen, kombiniert mit der M\u00f6glichkeit diese Tests \u00fcber Spring flexibel konfigurieren zu k\u00f6nnen. Hinzu kam ein Selenium-Grid, welches die Browser-Instanzen zur Verf\u00fcgung stellt und eine Statistikkomponenten zum Erfassen verschiedener Zeitstempel innerhalb der Browser (\u00fcber JavaScript) und in den Tests selbst.<br />\nDer Vortrag stellt die technischen Details des Setups dar, berichtet \u00fcber die von uns gesammelten Erfahrungen und soll die Zuh\u00f6rer in die Lage versetzen das Setup in eigenen Projekten zu verwenden.</p>\n'),
   TIMESTAMP '2016-05-15 16:30:00.0', '', TIMESTAMP '2016-05-15 15:45:00.0', 'Lasttests mit Selenium',
   'Java User Group'),
(35, STRINGDECODE('<p>Microservices sind nicht nur ein Hype: Sie stellen einige grundlegende Annahmen der Software-Architekturen in Frage. Soll man Redundanzen wirklich vermeiden? Ist eine saubere Architektur wirklich der Schl\u00fcssel zu langfristiger Wartbarkeit? Diese Keynote handelt davon, wie Microservices mit unorthodoxen Ans\u00e4tzen eine produktive Software-Entwicklung erlauben und wie wir Architektur auch jenseits von Microservices neu denken m\u00fcssen.</p>\n'), TIMESTAMP '2016-05-15 17:45:00.0', '', TIMESTAMP '2016-05-15 16:45:00.0', 'Microservices: Redundanz = Wartbarkeit!', 'Keynote'),
  (36, STRINGDECODE(
      '<p>Sichere Anwendungsentwicklung: Muss ich mich mit dem Thema jetzt besch\u00e4ftigen? </p>\n<p>M\u00fcssen nicht - aber wer will schon abwarten, bis das eigene System gehackt ist und kritische Daten im Dark Web gehandelt werden?  Wenn, wie beim Trend des \u201eInternet of Things\u201c, auf alles \u00fcber das Internet zugreifbar ist, dann wird es umso interessanter, wer da zugreifen kann. </p>\n<p>Deshalb wollen wir beim World Caf\u00e9 diskutieren, was wir beachten m\u00fcssen/sollten/wollen, wenn bisher unternehmensinterne Dienste und Datenbanken in die Cloud wandern. Inwieweit beeinflussen die Trends (Mobile, Cloud, IoT) bereits mein Tagesgesch\u00e4ft? Wie bereitet sich mein Unternehmen auf die neuen Sicherheits-Herausforderungen vor \u2013 wie steht es um Mitarbeiter-Schulungen, Threat Risk Analysis, Penetration Tests etc.? Und vor allem: Welche Kernkompetenzen in Punkto Datenschutz und Sicherheit braucht in Zukunft jede Entwicklerin und jeder Entwickler?</p>\n<p>Ort des Geschehens ist traditionell das \u201egelbeseiten \u2013 caf\u00e9-bar-lounge\u201c. Wir freuen uns auf Diskussionen, die spannend werden. Da sind wir uns sicher.</p>\n'),
   TIMESTAMP '2016-05-15 19:30:00.0', '', TIMESTAMP '2016-05-15 18:00:00.0',
   STRINGDECODE('World Caf\u00e9 - Sichere Anwendungsentwicklung im Zeitalter von Mobile, Cloud, Internet of Things'),
   'Abendevent');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (37, '', TIMESTAMP '2016-05-16 09:15:00.0', '', TIMESTAMP '2016-05-16 09:00:00.0',
   STRINGDECODE('Begr\u00fc\u00dfung'), STRINGDECODE('Begr\u00fc\u00dfung')),
  (38, STRINGDECODE(
      '<p>Scrum has been around for two decades. During the first decade of agile, the adoption of agile and Scrum have grown incredibly. The survival and prosperity of many people and organizations depend on software. Scrum has been a key tool for teams and organizations to deal with the increased criticality of software.<br />\nBut the dependence of businesses and society on software has increased even more. Software is eating the world. Complexity and unpredictability continue to increase. The urgency to improve remains.<br />\nThe key for future success is still Scrum. We are not Done with Scrum just yet. The key to employing Scrum professionally is creating Done Increments of product, where \u201cDone\u201d reflects releasable in production. This requires professional development, proper practices and standards, cross-functional collaboration, and inner-Sprint feedback loops. It might take another two decades to actually get there.<br />\nIn his session, Gunther Verheyen explores the system called \u2018Scrum\u2019, how it has helped, and how it can continue to help.<br />\nGunther is a seasoned Scrum professional. He works for Scrum.org, the home of Scrum, where he shepherds the Professional series. He represents Scrum.org and Scrum co-creator Ken Schwaber in Europe.</p>\n'),
   TIMESTAMP '2016-05-16 10:15:00.0', '', TIMESTAMP '2016-05-16 09:15:00.0',
   'The future present of Scrum (Are we Done yet?)', 'Keynote'),
(39, '', TIMESTAMP '2016-05-16 10:45:00.0', '', TIMESTAMP '2016-05-16 10:15:00.0', 'Kaffeepause', 'Pause'),
  (40, STRINGDECODE(
      '<p>Viele Organisationen starten \u201eagile Initiativen\u201c,  um Effektivit\u00e4t und Effizienz im IT- Produktmanagement und in der Softwareentwicklung zu erh\u00f6hen. Nach einer Weile sterben viele dieser Initiativen, schlafen ein oder bleiben zwar am Leben, aber nur als blasse Schatten. Dabei geht viel Geld verloren, noch mehr Motivation und vor allem schwindet der Glaube an den Erfolg dieser Versuche, neue Wege zu gehen. Sogar das Wort \u201eagil\u201c wird verbannt und darf in einem solchen Zusammenhang nicht mehr benutzt werden.</p>\n<p>Zugegeben: Ein professioneller Agile Change ist keine einfache Aufgabe  \u2500 unm\u00f6glich ist sie aber auch nicht!<br />\nDas Verstehen der Schwachstellen in den zu ver\u00e4ndernden Bereichen, das Verstehen der Komplexit\u00e4t und des Umfangs dieses Vorhabens in einer Organisation sowie Verst\u00e4ndnis f\u00fcr die Rolle des Managements bei diesem Change, das sind die ersten essentiellen Schritte nachhaltiger Initiativen. Daraus folgen die notwendigen Ziele und \u201eKey Actions\u201c sowie deren Umsetzung innerhalb effizienter Strukturen und Prozesse.</p>\n<p>Aus der Praxis heraus diskutiere ich mit Ihnen in meinem Vortrag diese Herausforderung. Dazu beziehen wir uns auf ein Modell und er\u00f6rtern die notwendigen Key Actions und somit auch die Rolle des Managements im Professionell Change. Denn die ist absolut entscheidend.</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Rebland', TIMESTAMP '2016-05-16 10:45:00.0',
   'Professionell Agile Change im IT-Umfeld. Eine Management Aufgabe!', 'Innovation / Enterprise Agile'),
  (41, STRINGDECODE(
      '<p>Selbstbestimmung auf einem Atom-U-Boot?<br />\nUnternehmensstrukturen ohne Manager?<br />\nMitarbeiter, die sich ihre Projekte selbst suchen?<br />\nKlingt nach eher wirren Strukturen? Im Gegenteil!<br />\nDas fragliche U-Boot ist eines der erfolgreichsten Schiffe der U.S. Navy.<br />\nDas Maschinenbau-Unternehmen Semco wurde in Brasilien mit seinem revolution\u00e4ren F\u00fchrungsmodell zum Wunscharbeitgeber.<br />\nUnd der Spieleentwickler Valve hat beste Erfahrungen damit gemacht, Mitarbeiter ihre Projekte selbst aussuchen zu lassen.<br />\nSolche Erfolgsgeschichten zeigen uns, wie Selbstorganisation, revolution\u00e4re F\u00fchrungsmodelle und Holocracy eingesetzt werden, was die jeweiligen Ans\u00e4tze ausmacht und warum sie erfolgreich sind.<br />\nIm Vortrag werden wir ausgew\u00e4hlte Erfolgsgeschichten von Selbstorganisation und agiler F\u00fchrung vorstellen, analysieren und diskutieren, was wir daraus lernen k\u00f6nnen.</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Kraichgau', TIMESTAMP '2016-05-16 10:45:00.0',
   STRINGDECODE('Selbst sind die Mitarbeiter \u2013 Erfolgsgeschichten von Selbstorganisation'), 'Agile Teams');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (42, STRINGDECODE(
      '<p>Ein Grundbaustein agiler Projekte ist kontinuierliches Feedback, z.B. durch Pair Programming, Continuous Integration oder automatisierte Tests. Daneben gibt es in den meisten Teams eine Menge von Design- und Architekturvorgaben und Entwicklungsrichtlinien (z.B. Namenskonventionen oder zyklenfreie Paketstrukturen) mit dem Ziel, Codekomplexit\u00e4t zu reduzieren und Wartbarkeit zu gew\u00e4hrleisten. Solche Regeln werden oft nur manuell gepr\u00fcft, was fehleranf\u00e4llig und m\u00fchsam sein kann. Wir zeigen exemplarisch anhand einiger Tools und Vorgehensweisen aus dem Entwickleralltag, wie diese mit wenig Aufwand automatisiert gepr\u00fcft werden k\u00f6nnen. Die Teilnehmer k\u00f6nnen einige Ideen und Anregungen mitnehmen, die zu regelm\u00e4\u00dfigem und schnellem Feedback zur Einhaltung von Projektrichtlinien f\u00fchren.</p>\n<p>Tags: Clean Code, Richtlinien, Checks<br />\nZielpublikum: Entwickler, Architekten<br />\nBeispielcode: Java</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Fidelitas', TIMESTAMP '2016-05-16 10:45:00.0',
   STRINGDECODE('Architekturvorgaben und Entwicklungsrichtlinien automatisiert pr\u00fcfen'), 'Architektur'),
  (43, STRINGDECODE(
      '<p>Der Vortrag zeigt die Einsatzm\u00f6glichekiten der Open Source L\u00f6sungen Eclipse Vorto zur Integration. Er zielt insbesondere auf die Schnittstelle zwischen Entwicklern eingebetteter Systeme, mobiler Anwendungen, Cloud L\u00f6sungen sowie Architekten f\u00fcr IoT L\u00f6sungen.<br />\nEclipse Vorto erlaubt die Erstellung, das Management und die Ver\u00f6ffentlichung von Ger\u00e4tebeschreibungen (sogenannte Informationsmodelle). Mit Hilfe der Vorto Code Generator Infrastruktur k\u00f6nnen diese Informationsmodelle in andere Formate \u00fcberf\u00fchrt werden. Beispiele f\u00fcr solche Formate sind XML, Java Code oder auch Markdown und LaTeX. Dies erm\u00f6glicht unter Anderem die vollautomatische Erzeugung eines auf Eclipse Californium basierenden CoAP Servers und zugeh\u00f6rigen Clients, welche \u00fcber die im Informationsmodell beschriebenen Schnittstelle kommunizieren.<br />\nDer Vortrag zeigt den Aufbau von Vorto und welche Vorteile die Werkzeugunterst\u00fctzung hinsichtlich Fehlervermeidung und Reduzierung von repetitiver Arbeit bietet.</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Schwarzwald', TIMESTAMP '2016-05-16 10:45:00.0', 'Eclipse Vorto',
   STRINGDECODE('Blick \u00fcber den Tellerrand')),
  (44, STRINGDECODE(
      '<p>Observational Testing ist ein effizienter Testansatz, der f\u00fcr Legacy-Systeme (Systeme ohne/ausreichende Tests) mit Benutzeroberfl\u00e4chen schnell ein Sicherheitsnetz aufspannt um die Auswirkung von \u00c4nderungen zu kontrollieren. Wir haben dazu ein Open Source Projekt entwickelt, welches wir erstmalig auf dem Karlsruher Entwicklertag pr\u00e4sentieren wollen. Wir suchen weitere Begeisterte, die mit uns dazu beitragen wollen, den Stand der Qualit\u00e4tssicherung zu Verbessern und an dem Projekt mitarbeiten. Au\u00dferdem ist nat\u00fcrlich jeder herzlich willkommen unsere Software auszuprobieren oder bei sich im Projekt einzusetzen.</p>\n<p>ReTest (<a href=\"http://www.retest.de\">www.retest.de</a>) steht f\u00fcr einen innovativen KI-basierten Ansatz zur Automation von GUI-Regressions-Tests. Dabei liegt der Fokus auf der einfachen Anwendbarkeit des Werkzeuges mit schnellen Ergebnissen (und dementsprechend einem hohen ROI).</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Baden', TIMESTAMP '2016-05-16 10:45:00.0',
   STRINGDECODE('Observational Testing \u2013 Open Source Made in Karlsruhe'), 'Testing'),
  (45, STRINGDECODE(
      '<p>You already know how to conduct the \"Silver Bullet Retrospective\" that solves all the problems you might be facing in your really big software development project? Then this experience report is not for you. If, on the other hand, you want to hear a real-world story about the challenges, pitfalls and success stories of a project retrospective for a big software development project in a large software development organization, then this talk might be right for you.<br />\nWe''ll tell you about some challenges that came about when, within our big software development department that is well on its way to employing and living Lean and Agile principles (using Scrum, Kanban and other Agile methodologies) with more than collaborating 20 Scrum Teams, an important development project was driven under high pressure both from the customer side and from top level management. We faced questions like \"How can we continue living Lean and Agile principles while under pressure?\", \"How do we come to a sustainable velocity that fits the needs of both the employees and the customers?\", and \"How do we have to adapt our approach to planning, within our scaled Scrum setup?\".<br />\nWe addressed these topics in a big Open-Space-style Retrospective --- and in this talk we''ll tell you how we put that into action, what preparations we found necessary, how we conducted the event, and --- most importantly --- what we did in order to sustainably implement the findings from the Retro.<br />\nAs a key take-away we''ll share our learnings on success factors of such a big project retrospective.</p>\n'),
   TIMESTAMP '2016-05-16 11:30:00.0', 'Raum Rheinauen', TIMESTAMP '2016-05-16 10:45:00.0',
   'Large scale project retrospectives from the trenches. An experience report from a big software company',
   'Agile Skalierung');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (46, STRINGDECODE(
      '<p>Da\u00df die Innovationsf\u00e4higkeit ein wesentlicher Erfolgsfaktor sowohl f\u00fcr Unternehmen als auch f\u00fcr Volkswirtschaften ist, ist eine seit vielen Jahren gut belegte Tatsache. Somit gilt es, Innovationsf\u00e4higkeit zu erzeugen und das Potential in tats\u00e4chliche Innovationen umzum\u00fcnzen. Es geht um Ideenfindung, Umsetzung, Einf\u00fchrung und Weiterentwicklung. Zu den klassischen, eher schwergewichtigen, Vorgehensmodellen mit einem \u201eBig-Design-Upfront\u201c-Ansatz haben sich in den letzten Jahren schlanke Herangehensweisen wie Design Thinking und Lean Startup gesellt. Aber auch diese haben ein gewisses strukturelles \u201eGewicht\u201c und es stellt sich die Frage, ob in bestimmten Situationen noch schlanker und noch flexibler gearbeitet werden kann.<br />\nAm Beispiel des Open-Data-Projekts \u201eSchatzkarte\u201c wollen wir einen solchen extrem leichtgewichtigen Ansatz vorstellen. Kern des Vorgehens ist das Ausgehen von dem Vorhandenen (egal wie minimal es sein mag), das Experiment und die sofortige Anpassung als Reaktion auf die von Entwicklern und Anwendern gemachten Erfahrungen. Dabei entsteht die Software aus dem initialen Keim in inkrementellen, stets auf Produktionsreife ausgerichteten Schritten. Da hier kein Prototyp sondern Produktivsoftware entsteht, ist es nicht so sehr ein \u201eLearning by Doing\u201c als vielmehr ein \u201eDoing by Learning\u201c.<br />\nWas ist eine Open-Data-Anwendung? Kurz gesagt, gibt sie den B\u00fcrgern die M\u00f6glichkeit, Regierung und Verwaltung \u00fcber die Schulter zu schauen und nachzuvollziehen, wozu diese ihre Steuergelder verwenden. Denn nur der informierte B\u00fcrger kann als m\u00fcndiger B\u00fcrger seine demokratischen Rechte wahrnehmen. Die \u201eSchatzkarte\u201c ist eine Open-Data-Anwendung, die die Haushalte der St\u00e4dte L\u00fcbeck und Gl\u00fcckstadt zug\u00e4nglich macht.<br />\nF\u00fcr ein solches Projekt reicht es nat\u00fcrlich nicht, einfach ein paar Tabellen und Tortendiagramme ins Internet zu stellen. Es mu\u00df die komplexe Fachlichkeit der \u00f6ffentlichen Haushaltslegung durchdrungen und in Code umgesetzt werden. Das Datenmaterial mu\u00df entsprechend strukturiert und persistiert werden. Dem Nutzer m\u00fcssen m\u00e4chtige und doch einfache Werkzeuge f\u00fcr die Abfrage und Analyse der Daten zur Verf\u00fcgung gestellt und die Ergebnisse sinnf\u00e4llig visualisiert werden. Und zuguterletzt steht und f\u00e4llt ein jedes System mit der optischen Pr\u00e4sentation: nur attraktive Anwendungen finden auch breiten Zuspruch. Zweck, Logik und UX bestimmen sich gegenseitig.<br />\nDa wir auf dem Entwicklertag sind, soll die Praxis nicht zu kurz kommen. An Beispielen zeigen wir live, wie die Aufgabenstellungen in der integrierten Smalltalk-JavaScript-Umgebung \u201eAppeX\u201c gel\u00f6st werden. Die Zuh\u00f6rer bekommen einen Eindruck vom engen Code-Debug-Deploy-Zyklus, inklusive Server-Update bei laufendem Betrieb.</p>\n<p>Zielpublikum: Entwickler, Projektleiter, Berater, alle<br />\nVorkenntnisse: keine<br />\nCode-Beispiele und Demo: in Smalltalk und JavaScript</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Rebland', TIMESTAMP '2016-05-16 11:45:00.0', STRINGDECODE(
       '\u201eDoing by Learning\u201c \u2013 Innovation durch erfahrungsgetriebene Softwareentwicklung am Beispiel des Open - Data - Projektes \u201eSchatzkarte\u201c'),
   'Innovation / Enterprise Agile'),
  (47, STRINGDECODE(
      '<p>Big Design up front passt nicht zur agilen Vorgehensweise, aber wieviel Architektur-Arbeit ist im Voraus wirklich erforderlich? Wie gehe ich dabei vor und welche Ergebnisse sollen dabei entstehen?<br />\nEgal ob Neuentwicklung oder gr\u00f6\u00dfere Erweiterung, eine Phase 0 stellt den geeigneten Zeitraum f\u00fcr den Beginn der Architekturarbeit dar. Die ersten Ideen flie\u00dfen in die Architektur-Vision ein und dienen als Startpunkt f\u00fcr die Entwicklung. Entwicklungsbegleitend wird die Architektur im Team weitergepflegt und die Ergebnisse flie\u00dfen in die Architekturdokumentation ein.<br />\nBringen Sie Stift und Papier mit. Dann werden Sie im Rahmen einer Speed-Kata ihre eigene Erfahrung mit der Erstellung einer Architektur-Vision sammeln k\u00f6nnen.</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Fidelitas', TIMESTAMP '2016-05-16 11:45:00.0',
   'Gerade genug Architektur vorneweg - Zur eigenen Architektur-Vision in 16 Minuten', 'Architektur');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (48, STRINGDECODE(
      '<p>Als Objektorientierter Entwickler mit Hintergrund in Java, C# usw. war ich 6 Jahre in SAP-Projekten mit ABAP unterwegs. Dabei habe ich gro\u00dfe Schmerzen erlitten, aber auch interessante Konzepte kennengelernt. In dem Vortrag zeige ich meinen Reisebericht.<br />\nSAP ist /die/ Standard-Software f\u00fcr Kaufm\u00e4nnische Anwendungen. In der Folge wird auch die zu SAP geh\u00f6rige Programmiersprache ABAP viel von Kaufleuten eingesetzt. In diesem Vortrag schauen wir uns die Programmiersprache durch die Augen des Entwicklers mit Informatik-Hintergrund an.<br />\nABAP stand urspr\u00fcnglich f\u00fcr ''Allgemeiner Berichtsaufbereitungsprozessor''. Heute kann die Sprache aber weitaus mehr als Berichte zu erzeugen und zu verarbeiten. Die Sprache ist Turing-m\u00e4chtig und \u00fcber die Zeit haben verschiedene jeweils moderne Technologien und Konzepte Einzug in die Sprache gehalten.<br />\nDieser Vortrag nimmt den Teilnehmer mit auf eine Rundfahrt durch ein Programmiersprache, die historisch gewachsen ist; die viel Licht und viel Schatten enth\u00e4lt und die produktiv im Einsatz ist bei den gr\u00f6\u00dften Unternehmen dieser Welt.<br />\nDer Vortrag wird im \"Lessig-Style\" gehalten werden. (<a href=\"https://youtu.be/Sop4LwsP090\">https://youtu.be/Sop4LwsP090</a>)</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Schwarzwald', TIMESTAMP '2016-05-16 11:45:00.0',
   'Sechs Jahre ABAP - Eine Reise ins Unbekannte', STRINGDECODE('Blick \u00fcber den Tellerrand')),
  (49, STRINGDECODE(
      '<p>Mocking-Frameworks geh\u00f6ren heute in den meisten Projekten zum Standard-Handwerkszeug. Man setzt sie ein, wenn man die \"echte\" Implementierung in den Tests gerade nicht benutzen kann oder will.<br />\nIn diesem Vortrag m\u00f6chten wir einen Schritt zur\u00fcck machen und der Frage nachgehen, wann der Einsatz eines Mocking-Frameworks hilfreich ist und wann nicht. Zusammen m\u00f6chten wir den Umgang mit Test Doubles reflektieren und aus einer anderen Perspektive betrachten. Welche Alternativen bieten sich f\u00fcr F\u00e4lle an in denen Mocking-Frameworks nicht die erste Wahl sind?<br />\nAusgehend von den historischen Wurzeln von Mocking-Frameworks zeigen wir was die urspr\u00fcngliche Motivation f\u00fcr dieses Hilfsmittel war. Dabei werden wir anhand von Code-Beispielen auf die zwei vorherrschenden Denkschulen des Test Driven Developments und Designs eingehen und wie diese zu Mocking-Frameworks stehen. Daneben wollen wir auch auf gute und schlechte Bespiele f\u00fcr den Umgang mit Mocks eingehen und die Rahmenbedingungen und Konsequenzen der Verwendung von Mocking-Frameworks er\u00f6rtern.<br />\nDer Vortrag richtet sich prim\u00e4r an Entwickler, die bereits etwas Erfahrung mit Unit-Tests und Test Doubles haben. Die verwendeten Code-Beispiele sind in Java geschrieben.</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Baden', TIMESTAMP '2016-05-16 11:45:00.0',
   'Wer braucht eigentlich ein Mocking-Framework?', 'Testing'),
  (50, STRINGDECODE(
      '<p>Regelm\u00e4\u00dfige Refinements sind eine kluge Praktik. Besonders, um \u00fcberbordende Planungsphasen zu verk\u00fcrzen, ohne in einen Ad-Hoc Zustand zu verfallen. Nur: Wie funktionieren sie effizient, wenn man auf einmal nicht mehr ein, sondern acht Teams hat? Und besonders, wenn das Projekt nicht co-located ist. Was jetzt? Wir zeigen, wie man die Ziele des Refinements in einer skalierten Umgebung erreichen kann. Dazu beantworten wir als Erstes die Frage, wie diese Ziele \u00fcberhaupt aussehen. Und was mit mehreren Teams, Fachbereichen und PO, noch funktioniert und was nicht. Dazu beziehen wir uns auf ein aktuelles Projekt, in dem aus gro\u00dfen reinen \u201eSch\u00e4tzmeetings\u201c ein iterativer Inspect & Adapt Zyklus wurde. Ganz real und nicht nur in der Theorie. Wir hoffen, damit einige Anregungen zu geben, die sich dann auch in anderen Projekten n\u00fctzlich erweisen.</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Rheinauen', TIMESTAMP '2016-05-16 11:45:00.0', 'Refinements @ Scale',
   'Agile Skalierung');
INSERT INTO PUBLIC.EVENT (ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (51, STRINGDECODE(
      '<p>Cross-funktionale Teams sind eine anerkannte Methode in der agilen Software-Entwicklung. In diesem Vortrag m\u00f6chten wir darauf eingehen, wie cross-funktionale Teams bei Citrix funktionieren und welche Beobachtungen wir im Hinblick auf Team-Identit\u00e4t, Qualit\u00e4t und Effektivit\u00e4t gemacht haben. Wir m\u00f6chten die unterschiedlichen Implementierung vergleichen und unserer Erkenntnisse teilen. Dar\u00fcber hinaus gehen wir darauf ein, warum cross-funktionale Teams f\u00fcr Citrix ein Kernelement zur Steigerung der Wettbewerbsf\u00e4higkeit und Innovationskraft sind.<br />\nAbstract: Software Unternehmen k\u00f6nnen am Markt nur \u00fcberleben, wenn Sie langfristig handlungsf\u00e4hig und innovationstark sind. Dies kann unserer Ansicht nach genau dann funktionieren, wenn agile Prinzipien in allen Bereichen, von der Softwareerstellung bis zur Auslieferung, konsequent umgesetzt werden. Funktional gemischte Teams sind eines - wenn nicht das - Kernelement um dieses Ziel zu erreichen.<br />\nDie Idee basiert auf der Einsicht, dass klassische Team-Organisationen nur bis zu einem gewissen Grad Marktanforderungen mit ihren immer k\u00fcrzer werdenden Innovationszyklen gerecht werden k\u00f6nnen.<br />\nIm Vortrag stellen wir anhand von Beispielen mehrere cross-funktionale Team-Konstellationen vor. Dabei zeigen wir Vor- und Nachteile der verschiedenen Konstellationen auf und nutzen Produkte zur Erl\u00e4uterung der Team-Kontexte.<br />\nDie cross-funktionalen Teams, von denen wir berichten, liefern teilweise mehrmals w\u00f6chentlich neue Produktinkremente f\u00fcr Live-Systeme aus. Sie k\u00f6nnen neue Features in sehr kurzer Zeit produktiv umsetzen und erhalten schnelle R\u00fcckmeldungen von Kunden. Wir arbeiten im Vortrag Herausforderungen w\u00e4hrend der Transformation in die cross-funktionale Arbeitsweise heraus.<br />\nCross-funktionale Teams, wie wir sie vorstellen, sind eine zwingende Voraussetzung um \u201cContinuous Delivery\u201d realisieren zu k\u00f6nnen. Bei vollst\u00e4ndiger Umsetzung werden damit software-produzierende Unternehmen in die Lage versetzt, schnell auf Marktbed\u00fcrfnisse reagieren zu k\u00f6nnen.<br />\nDer Zuh\u00f6rer wird Blaupausen, Best Practices und individuelle Faktoren kennen lernen, die cross-funktionale Teams zu Erfolg f\u00fchren oder ihnen im Weg stehen.</p>\n'),
   TIMESTAMP '2016-05-16 12:30:00.0', 'Raum Kraichgau', TIMESTAMP '2016-05-16 11:45:00.0',
   'Cross-funktionale agile Teams: Erfahrungsbericht', 'Agile Teams'),
(52, '', TIMESTAMP '2016-05-16 13:30:00.0', '', TIMESTAMP '2016-05-16 12:30:00.0', 'Mittagspause', 'Pause'),
  (53, STRINGDECODE(
      '<p>Bei \u201cfertigen\u201d Features sind h\u00e4ufig noch etliche Aufgaben zu erledigen, bis diese wirklich Wert beim Kunden erzeugen. All diese \u201cundones\u201d erh\u00f6hen das Risiko und wir f\u00fcttern damit unser \u201cUndone-Monster\u201d. Je gr\u00f6\u00dfer es wird, desto gef\u00e4hrlicher wird es und kann in Form von h\u00f6heren Kosten, Terminverschiebung oder schlechter Qualit\u00e4t zuschlagen. Wir zeigen, wie das Zielbild der Wertsch\u00f6pfungskette vom Business zum Kunden mit Agile und DevOps aussehen kann, und wie ihr damit das Undone-Monster im Zaum halten k\u00f6nnt.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Kraichgau', TIMESTAMP '2016-05-16 13:30:00.0',
   STRINGDECODE('Agile & DevOps: Don\u2019t feed the Undone-Monster'), 'Agile Teams'),
  (54, STRINGDECODE(
      '<p>H\u00e4ufig beginnen Versuche, gr\u00f6\u00dfere bestehende Unternehmen zu agilisieren, mit der Einf\u00fchrung von SW-Entwicklungsmethoden wie Scrum oder Kanban. In der heutigen Zeit ist das aufgrund der vielen Praxisbeispiele, Literatur und verf\u00fcgbaren Beratungsdienstleistungen ein beherrschbarer Schritt. Auch wenn die agile Methodik alleine schon einen Mehrwert bringen kann, entfaltet sich die wahre Kraft des agilen Ansatzes jedoch erst, wenn auch die Unternehmenskultur und das F\u00fchrungssystem folgen \u2013 ein ungleich komplexerer Schritt.<br />\nIn diesem Vortrag liefert Joachim Ritter, Leiter eines Produktentwicklungsbereichs der 1&1 Internet SE, einen offenen, ehrlichen und unterhaltsamen Einblick in das aktuell real laufende Experiment, die F\u00fchrungskultur und das Wertesystem in seinem Bereich aktiv hin zu einer neuen Arbeitswelt zu gestalten. Der Zuh\u00f6rer bekommt einen Einblick \u00fcber die Konzepte und Erfahrungen, die Teilerfolge und blutige Nasen, die 1&1 bei diesem Wandel einsetzt bzw. sammelt.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Rebland', TIMESTAMP '2016-05-16 13:30:00.0',
   'Der Weg zu einer neuen Arbeitswelt - ein Praxisbeispiel', 'Innovation / Enterprise Agile');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (55, STRINGDECODE(
      '<p>Continuous Delivery, die Containertechnologie Docker und Micro Services sind in aller Munde. Wie kannst du diese Trends aufnehmen und davon in deinem gewachsenen IT-Umfeld profitieren?<br />\nDer Vortrag stellt ein Szenario vor, in dem bestehende, produktive Anwendungen automatisiert auf verschiedenen Versionen einer Anwendungsplattform getestet werden. Am praktischen Beispiel wird das Zusammenspiel der Containertechnologie Docker und des Build Servers Jenkins demonstriert. Durch den Einsatz von Praktiken aus dem Continuous Delivery erreichen wir ein kontinuierliches und direktes Feedback. Dieser Use Case zeigt, wie Docker auch in einem noch nicht auf Micro Services umgestellten Umfeld einen direkten Mehrwert bietet und erste Erfahrungen mit diesen neuen Technologien gesammelt werden k\u00f6nnen.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Fidelitas', TIMESTAMP '2016-05-16 13:30:00.0',
   'Von Schwergewichten und Walfischen - automatische Plattformtests mit Docker', 'Architektur'),
  (56, STRINGDECODE(
      '<p>Wer in den 90ern ein Kind war, kenn ihn: Der Gameboy. Er ist das Symbol f\u00fcr den Beginn der Handheld-Spielekonsolen und gilt mittlerweile als Kultobjekt. Heute werden Gameboys auf Speichern, in Kellern oder in Kisten gefunden und dann \u00fcber eBay oder auf dem Flohmarkt verkauft. Und dort f\u00e4ngt die Geschichte an. Mit nur einem Gameboy Geh\u00e4use, einem RaspberryPi und wenigen anderen Komponenten l\u00e4sst sich aus dem Gameboy eine Handheld-Retro-Konsole bauen. Auf diese Weise habe ich zwei SuperPiBoy''s umgebaut und m\u00f6chte Dich zum Nachbau anleiten. In diesem Vortrag erz\u00e4hle ich wie aus einem alten Geh\u00e4use ein fertiger SuperPiBoy mit Maus und WLAN werden kann.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Schwarzwald', TIMESTAMP '2016-05-16 13:30:00.0', 'Build a SuperPiBoy',
   STRINGDECODE('Blick \u00fcber den Tellerrand')),
  (57, STRINGDECODE(
      '<p>Um Single-Page-Applikationen professionell einsetzen zu k\u00f6nnen, ist eine kontinuierliche Qualit\u00e4tssicherung unerl\u00e4sslich. In der von Mike Cohn entwickelten Testpyramide sind dabei neben Unit- und Integrationstests auch Akzeptanztests ein wichtiger Bestandteil. Einen Ansatz um Akzeptanztests zu automatisieren, liefert Specification by Example.<br />\nIn Specification by Example wird eine lebendige Dokumentation, bestehend aus User Stories und ausf\u00fchrbaren Beispielen, zu einem Softwaresystem erzeugt. Die Beispiele k\u00f6nnen f\u00fcr Single-Page-Applikationen mit Hilfe des Frameworks Cucumber spezifiziert und mit entsprechenden automatisierten Testf\u00e4llen verkn\u00fcpft werden. Diese Beispiele k\u00f6nnen dann unter anderem als Akzeptanzkriterien dienen.<br />\nF\u00fcr die Automatisierung der Testf\u00e4lle eignet sich Protractor, eine f\u00fcr Single-Page-Applikationen entwickelte Erweiterung f\u00fcr Selenium. Protractor wird als Testing-Framework sowohl f\u00fcr Angular 1, Angular 2 als auch alternative Frameworks wie beispielsweise Aurelia empfohlen.<br />\nIn diesem Vortrag wird zun\u00e4chst auf die Grundlagen qualitativ hochwertiger Akzeptanztests eingegangen und g\u00e4ngige Testparadigmen pr\u00e4sentiert.<br />\nAnschlie\u00dfend werden in integrierten Live-Coding-Sessions die Frameworks Protractor und Cucumber anhand praktischer Beispiele vorgestellt.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Baden', TIMESTAMP '2016-05-16 13:30:00.0',
   'Automatisierte Akzeptanztests bei Single-Page-Applikationen mit Angular, Angular 2 und Aurelia', 'Testing'),
  (58, STRINGDECODE(
      '<p>Kommunikation ist integraler Bestandteil und wesentlicher Erfolgsfaktor agiler Teams. Doch wie sieht es damit in verteilten Teams aus?<br />\nIm verteilten Team m\u00fcssen wir die eingeschr\u00e4nkte Kommunikation bewusst als Hindernis akzeptieren. Nur wenn wir st\u00e4ndig aktiv an ihrer Aufrechterhaltung und Verbesserung arbeiten, kann ein Team \u00fcber mehrere Standorte hinweg \u00e4hnlich produktiv sein wie ein Vor-Ort-Team und zu einem High Performance Team werden. Daher k\u00f6nnen Tools alleine diese Herausforderung auch nicht l\u00f6sen. Erst, wenn alle Teammitglieder ein passendes Mindset entwickeln, kann ein verteiltes Projekt zum Erfolg werden.<br />\nDieser Vortrag ist zum einen Erfahrungsbericht, zum anderen Ratgeber zu Motivation, Organisation und F\u00f6rderung der Kommunikation in verteilten Teams.</p>\n'),
   TIMESTAMP '2016-05-16 14:15:00.0', 'Raum Rheinauen', TIMESTAMP '2016-05-16 13:30:00.0',
   'Reden ist Silber, im verteilten Team wird''s zu Gold', 'Agile Skalierung');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (59, STRINGDECODE(
      '<p>Das 4-Augen-Prinzip ist eine anerkannte Methode der Qualit\u00e4tssicherung, nicht nur in der agilen Softwareentwicklung. Dort wird dies meist \u00fcber Pair Programming oder eben Code Reviews praktiziert.<br />\nOft ist es jedoch l\u00e4stig, Code Reviews f\u00fcr jede noch so kleine \u00c4nderung durchzuf\u00fchren oder der Overhead durch die verwendeten Tools ist zu gro\u00df. Als Team, das Continuous Delivery praktiziert und gro\u00dfen Wert auf Softwarequalit\u00e4t legt, war es uns wichtig einen Prozess zu finden, der eine vollst\u00e4ndige Abdeckung von Code \u00c4nderungen durch Code Reviews gew\u00e4hrleistet, ohne uns bei der t\u00e4glichen Arbeit zu sehr zu behindern.<br />\nIm Vortrag wird unser Code Review Prozess vorgestellt, den wir seit \u00fcber einem Jahr mit Erfolg im Einsatz haben. Es werden die Vor- und Nachteile verschiedener Code Review Tools und Vorgehensweisen wie z.B. Pre-Merge und Post-Merge Reviews erkl\u00e4rt. Auch wird darauf eingegangen, wie sich durch unseren Prozess die Collective-Code-Ownership in unserem Team verst\u00e4rkt hat indem kontinuierlich \u00fcber Code-Konventionen diskutiert wird, was vor allem auch neue Teammitglieder schneller in unser Team integriert hat. Wenn jeder Commit von einem Kollegen am n\u00e4chsten Tag noch einmal gepr\u00fcft wird, stellt sich auch eher ein Verhalten ein, saubere, kleine und in sich abgeschlossene Commits zu verfassen. Zu guter Letzt sind viele fruchtbare Diskussionen entstanden, ohne die wir manchen Fehler nicht gefunden und viele Design-Entscheidungen zu sp\u00e4t ge\u00e4ndert h\u00e4tten.<br />\nDer Vortrag ist an alle gerichtet, die mit Entwicklungsteams zu tun haben und vor haben, bei sich Code Reviews einzuf\u00fchren bzw. dies schon getan haben, jedoch noch nach Inspirationen suchen ihren Code Review Prozess zu verbessern.</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Kraichgau', TIMESTAMP '2016-05-16 14:30:00.0', 'Continuous Code Review',
   'Agile Teams'),
  (60, STRINGDECODE(
      '<p>Man kann zwar an vielen Stellen nachlesen, wie man Architekturdokumentation strukturiert. Aber auf der Suche nach einer praktikablen Handhabung zur Erstellung und Pflege enden die meisten Versuche in der WYSIWYG-H\u00f6lle einer Textverarbeitung oder im tiefen Schlund eines Wikis. In diesem Vortrag wollen wir uns anschauen, wie aufbauend auf bestehenden Tools und Textformaten eine m\u00f6glichst redundanzfreie Dokumentation erstellt und f\u00fcr verschiedene Zielgruppen in ansprechenden Formaten ausgeliefert werden kann. Es wird dabei um Begriffe wie Continuous Documentation und Documentation as Code gehen.</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Fidelitas', TIMESTAMP '2016-05-16 14:30:00.0',
   'Kontinuierliche Architekturdokumentation im agilen Umfeld', 'Architektur'),
  (61, STRINGDECODE(
      '<p>Eine Woche \u2013 eigene Ideen umsetzen \u2013 freie Teamwahl: Dieses Konzept einer Innovation Week wurde bei der 1&1 in kleinem Rahmen bereits zum zweiten Mal erfolgreich durchgef\u00fchrt. Neben positiven Auswirkungen auf die Zusammenarbeit und den Zusammenhalt sind dabei spannende Projekte enstanden. In dieser Session stellen Michael M\u00fcller und Tobias Lindenmann die Erfolgskriterien und Fallstricke vor, zeigen, was bei der Organisation zu beachten ist und wieso es lohnenswert ist, eine vergleichbare Veranstaltung im eigenen Unternehmen zu etablieren.</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Rebland', TIMESTAMP '2016-05-16 14:30:00.0',
   STRINGDECODE('Innovation Week: 1 Woche f\u00fcr meine Idee'), 'Innovation / Enterprise Agile'),
  (62, STRINGDECODE(
      '<p>Builds schlagen fehl und keiner merkt\u2019s?<br />\nDie Code Qualit\u00e4t sinkt und keinen k\u00fcmmert\u2019s?<br />\nBugs stapeln sich und keiner reagiert?<br />\nDamit ist jetzt Schluss!<br />\nFeedback-Devices und Informations-Radiatoren helfen, diese Punkte im Blick zu behalten und schaffen Motivation, sie endlich anzugehen.<br />\nIn diesem beispielorientierten Vortrag pr\u00e4sentieren wir euch einen bunten Blumenstrau\u00df an Feedback-Devices aller Art, darunter sogar praktische Ideen f\u00fcr die Umsetzung im Projektalltag.<br />\nAnschlie\u00dfend folgt, speziell f\u00fcr die ersten Reihen, eine kleine Demo. ;)</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Schwarzwald', TIMESTAMP '2016-05-16 14:30:00.0',
   'Build gecrasht? Feuer frei! - USB-Raketenwerfer und andere Feedback Devices',
   STRINGDECODE('Blick \u00fcber den Tellerrand'));
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
  (63, STRINGDECODE(
      '<p>As a side effect of a digitalized world, software developers are under constant pressure to deliver new features very quickly, while maintaining high software quality. This becomes more and more challenging as the code grows in size and complexity. While agile development puts extra emphasis on writing tests that can be re-executed repeatedly, most programmers find themselves dealing with old code with not much documentation and not many tests in place.<br />\nIn this presentation, we will go over some opportunities and possibilities for automating software testing and debugging processes, and improving software quality in general. We will discuss methodologies for stress testing and compatibility testing in the context of Java applications, and will present delta debugging as a practical automated approach for debugging regression problems.<br />\nWe will wrap up the presentation by an overview of our experience on incorporating testing tools into our own software development process at Horus software GmbH. Our almost 10-years-old software product is a Java-based, multi-layer software consisting of a graphical UI, client, server, and database components. Horus regularly integrates contributions from external sources such as university theses and research projects. While such contributions make exciting software features, they are not always written by experienced programmers, which makes it particularly challenging for us to ensure the desired product quality. In this presentation, we report on practical approaches and ideas that help us test and evaluate our code at various levels, and thus improve our users\u2019 experience.</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Baden', TIMESTAMP '2016-05-16 14:30:00.0',
   'Opportunities for Automating Software Testing and Debugging Processes', 'Testing'),
  (64, STRINGDECODE(
      '<p>Nexus, das Skalierungsframework von Scrum.org, erweitert Scrum. Der Nexus Guide beschreibt das Framework und erkl\u00e4rt seine Bestandteile. Und genau wie beim Scrum Framework halten sich Ken Schwaber und Scrum.org mit Details zur konkreten, situativen Ausgestaltung bewusst zur\u00fcck. Und das ist gut so!<br />\nWir nehmen daher Nexus mit in die Praxis, genauer gesagt in eine professionelle Implementierung in zwei Projekten mit acht und drei Teams. Der Vortrag skizziert die Ausgangssituation, die Umsetzung und Herausforderungen auf dem Weg bis zum Ziel: ein \u201eIntegrated Done Increment\u201c pro Sprint zu liefern. Besonders gehen wir auf die Situationen mit mehreren Standorten und unterschiedlichen Technologien und Plattformen in den beiden Projekten ein. Dabei beschreiben wir die eingesetzten Praktiken hinsichtlich effektiver Scrum Meetings, die Nexus Integration Teams sowie den Refinement Cycle.</p>\n'),
   TIMESTAMP '2016-05-16 15:15:00.0', 'Raum Rheinauen', TIMESTAMP '2016-05-16 14:30:00.0',
   STRINGDECODE('Nexus in der Praxis. Ein Realit\u00e4tscheck'), 'Agile Skalierung'),
(65, '', TIMESTAMP '2016-05-16 15:45:00.0', '', TIMESTAMP '2016-05-16 15:15:00.0', 'Kaffeepause', 'Pause'),
(66, STRINGDECODE('<p>Viele Features die es bisher ausschlie\u00dflich in funktionalen Sprachen gegeben hat sind inzwischen in das Repertoire von objektorientierten Sprachen aufgenommen worden.<br />\nOb .NET, Java oder C++ - alle Entwickler k\u00f6nnen von funktionalen Denkans\u00e4tzen profitieren. .NET Entwickler sind durch Lambda Ausdr\u00fccke/ LINQ oder async/await inzwischen sogar schleichend an eine funktionale Denkweise herangef\u00fchrt worden.<br />\nDer Vortrag zeigt Technologie unabh\u00e4ngig, wie man funktionales Denken auf eine \u201eklassische\u201c objektorientierte Softwarearchitektur anwenden kann und welche Vor- aber auch Nachteile sich dadurch ergeben.<br />\nAnwendungslogik wird in wiederverwendbaren Funktionsbausteinen implementiert die nur noch \u00fcber Datenobjekte kommunizieren.<br />\nTopologische Abh\u00e4ngigkeiten zwischen Klassen werden eliminiert und nicht nur wie beim Einsatz eines DI-Containers dynamisch aufgel\u00f6st.<br />\nDas hat fundamentale Auswirkungen auf die Architektur eines Softwaresystems. SOLID Prinzipien? Leidvolle Mock-/ Stub-Orgien? Fehlanzeige.<br />\nNach einer Einf\u00fchrung in die Thematik anhand der typischen Probleme des OO-Designs geht der Vortrag auch auf Realisierungsm\u00f6glichkeiten wie Methoden Flows oder Event Based Components ein.<br />\nDer Vortrag schlie\u00dft mit einer Pro-Contra Diskussion und einem Blick in die Zukunft.</p>\n'), TIMESTAMP '2016-05-16 16:30:00.0', 'Raum Fidelitas', TIMESTAMP '2016-05-16 15:45:00.0', 'Das Beste aus zwei Welten - Funktionale SW Architektur und OOP', 'Architektur');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(67, STRINGDECODE('<p>Agile Softwareentwicklung \u2013 was nutzt es uns? Diese Frage wird h\u00e4ufig dem Management gestellt. Da liegt der Einsatz von Metriken auf der Hand. Doch worauf muss man beim Einsatz agiler Metriken achten? Was sind die Gefahren und Risiken beim Einsatz von Metriken?<br />\nIm Dialog zwischen Management und Agilem Coach werden verschiedene Metriken vorgestellt, bez\u00fcglich m\u00f6glicher Konsequenzen beleuchtet und in den Kontext agiler Werte gesetzt. Gemeinsam werden Dos and Don\u2019ts er\u00f6rtert, Best Practices geschildert und schlie\u00dflich potentiell geeignete Agile Metriken erarbeitet.</p>\n'), TIMESTAMP '2016-05-16 16:30:00.0', 'Raum Rebland', TIMESTAMP '2016-05-16 15:45:00.0', STRINGDECODE('Agile Metriken \u2013 Zwei Blickwinkel'), 'Innovation / Enterprise Agile'),
(68, STRINGDECODE('<p>Samstagabend. Zwei gute Kumpel philosophieren bei einer Flasche Wein \u00fcber Leben, Liebe und die neue hei\u00dfe Start-up-Idee. Sie wollen ein angestaubtes Finanzprodukt in eine neue smarte Software-L\u00f6sung gie\u00dfen. Voller Euphorie h\u00e4ngen sie am Montag ihren sicheren Job an den Haken, leihen sich vom reichen Onkel einen gro\u00dfen Berg Geld und stellen am Dienstag voller Ern\u00fcchterung fest \u2013 nix da!!!<br />\nAuf dem Weg zum Start-up werden viele Entscheidungen getroffen: Technologien werden ausgew\u00e4hlt, Prozesse etabliert, Anforderungen definiert. Und Architekturen...? Diese entstehen, vergehen und reifen. Wenn schlie\u00dflich Product-Owner und Rechtsanw\u00e4lte Best Friends werden, dann d\u00fcrfen auch die Entwickler stolz auf \u201eihr\u201c Produkt sein.<br />\nDer Vortrag \u201eHow to build a FinTech\u201c f\u00fchrt durch den Softwareentstehungsprozess von der gr\u00fcnen Wiese bis zum fertigen Produkt. Dabei beleuchten wir Vorgehen und Technologie gleicherma\u00dfen. Das vorgestellte Projektbeispiel beinhaltet unter anderem einen Wildfly Cluster, eine AngularJS Single-Page-Application und eine Ionic Mobile-App. Dar\u00fcber hinaus stellen wir Konzepte f\u00fcr Continuous Integration und -Delivery vor.</p>\n'), TIMESTAMP '2016-05-16 16:30:00.0', 'Raum Schwarzwald', TIMESTAMP '2016-05-16 15:45:00.0', STRINGDECODE('How to build a FinTech \u2013 Produktentwicklung 2.0'), STRINGDECODE('Blick \u00fcber den Tellerrand')),
(69, STRINGDECODE('<p>Einem klassisch durchgef\u00fchrten Gro\u00dfprojekt im Enterprise-Bereich stand nach der abgeschlossenen Realisierungsphase der gro\u00dfe Integrationstest durch die betroffenen Fachbereiche bevor. Auf Grund des Ausma\u00dfes des Projekts war dieser mit folgenden Risiken verbunden:<br />\n- Die Software wurde erstmals aus sechs Teilprojekten umfangreich integriert.<br />\n- Die Endanwender kamen gro\u00dfteils das erste Mal mit der Software in Ber\u00fchrung.<br />\n- Die Anzahl an Endanwendern aus den verschiedensten Fachbereichen war gro\u00df (insgesamt > 100 Mitarbeiter). Einzelne Testf\u00e4lle f\u00fcr Gesch\u00e4ftsprozesse mussten fast zehn verschiedene Abteilungen durchlaufen und so abgesegnet werden.<br />\n- Der gro\u00dfe Umfang an Testszenarien, die in insgesamt 8 Wochen durchgef\u00fchrt werden sollten.</p>\n<p>Auf die Frage, wie diesen Faktoren bestm\u00f6glich begegnet werden konnte, schlugen wir das Nutzen von agilen Methoden vor. Selbst in einem sp\u00e4ten Stadium oder zur Steuerung einzelner Phasen des Wasserfall-Vorgehens k\u00f6nnen die Vorteiler agiler Methoden (Fokus, Kommunikation, kontinuierliches Feedback) genutzt werden. Mit der Hilfe von umfassenden Workshops mit cross-funktionaler Beteiligung konnten wir bereits die Testf\u00e4lle so definieren, dass diese sp\u00e4ter durch die Tester mit Unterst\u00fctzung von Karteikarten und Taskboards effizient abgewickelt werden konnten. Parallel dazu halfen uns Dailys, die Impediments zu identifizieren, um die sich ein Agile Coach k\u00fcmmern konnte. Der hohe logistische und Aufwand f\u00fcr den Test konnte so in Grenzen gehalten und eine Vielzahl von Testf\u00e4llen effizient abgearbeitet werden.<br />\nDer Vortrag und das Fallbeispiel zeigen, wie Sie durch die Anwendung einzelner Elemente agiler Methoden und die Besinnung auf die Werte auch au\u00dferhalb der klassischen Softwareentwicklungsphase gro\u00dfe Vorteile erzielen k\u00f6nnen.</p>\n'), TIMESTAMP '2016-05-16 16:30:00.0', 'Raum Baden', TIMESTAMP '2016-05-16 15:45:00.0', STRINGDECODE('Mit agilen Prinzipien gro\u00dfe Integrationstests einfach managen'), 'Testing');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(70, STRINGDECODE('<p>Mal aussteigen. Raus aus dem Alltag und rauf auf den derzeit einzigen transatlantischen Frachtsegler \u2013 die Tres Hombres. Vom 1. Oktober bis 21.Dezember 2015 war ich auf nur 32m L\u00e4nge, 6m Breite, 3m Tiefgang unterwegs. Unter bis zu 19 Segeln an 2 Masten. Ohne Motor. Von Den Helder (NL) nach Barbados in der Karibik. Einmal \u00fcbers Gro\u00dfe Wasser.<br />\nMit einem Team, das aus 5 professionellen Seeleuten und bis zu 10 Newbies bzw. Trainees besteht, die sich vorher nicht kennen. Forming \u2013 Storming \u2013 Norming \u2013 Performing? Damit nicht genug \u2013 die Zusammensetzung kann sich in jedem Hafen, den wir anlaufen, ver\u00e4ndern. Geht\u2019s dann von vorne los ... Forming \u2013 Storming - ... ? Und keiner kann weg, wenn wir erst einmal drau\u00dfen auf See sind.<br />\nTeam extreme!<br />\nWie geht das aus? Was hilft dabei, an Bord zu bleiben? Was kann man, wenn es an die Grenzen geht, \u00fcber sich selbst, die anderen und die Zusammenarbeit im Team lernen? Und: Wie buchstabiert man Respekt? R \u2013 e \u2013 s \u2013 p \u2013 e \u2013 c \u2013 t?<br />\nDa ich den Coach in mir schwerlich zu Hause lassen konnte, war also mindestens ein Beobachter an Bord, der auf das Team schaute und lernte, lernte, lernte. Am Ende die Frage: Was kann man aus solchen Extremsituationen f\u00fcr die t\u00e4gliche Arbeit im (Entwickler-)Team mitnehmen?<br />\nEin Selbstversuch.</p>\n'), TIMESTAMP '2016-05-16 16:30:00.0', 'Raum Kraichgau', TIMESTAMP '2016-05-16 15:45:00.0', STRINGDECODE('Team Extreme, Oder: 15 Mann auf des toten Mannes Kiste \u2026'), 'Agile Teams'),
(71, STRINGDECODE('<p>It\u2019s sometimes said that agile grew out of the spirit of object orientation: its focus on change, on anthropomorphism, and on people. At the outset object-orientation was all about people. Alan Kay saw objects as a way for people to connect with their environments through their machines, and Trygve Reenskaug\u2019s Model-View-Controller provided the integration of people with their machines. Both were immersed in Doug Englebart\u2019s vision to connect people: where each window on the screen might represent a person in one''s circle of contacts, and Doug invented the mouse to be able to switch the focus of the human connection. Waterfall processes and tools became subordinated to individuals and interactions. Kay\u2019s objects were just externalized versions of the operational models active in peoples\u2019 heads \u2014 externalized representations that users would create themselves in a child-like programming language. As late as the 1980s people were still talking about anthropomorphic programming.</p>\n<p>Fast forward almost 30 years and we witness the formal birth of Agile. It was the nerd\u2019s revenge not only in its implementation emphasis, but also in its subtle amplification of the us-and-them dichotomy between customers and development. End users dropped out of the vocabulary, and software became an independent artefact that only had to be \u201cworking\u201d as a consideration separate from \u201cindividuals and interactions.\u201d The original OO focus on interactions between end-user individuals and their software became displaced by a focus on interactions between members of the vendor team or, at best, between the vendors in the customer, in the space of processes and tools. Agile replaced the original OO vision of end-user-cum-programmer with a programmer / customer dichotomy,. </p>\n<p>In the mean time object-orientation also devolved, replacing most of the original dynamics of networks of interacting objects of the end-user world with the classes of the development world. Object-think would take over the modeling world, architecture world, and programming world; Kay\u2019s operational models would be displaced by a Unified Modeling Language; networks of objects would be replaced by class hierarchies. Reasoning about the correctness of the result of an object network computation would be replaced by unit testing. The collective understanding of how to use MVC properly would become seriously eroded.</p>\n<p>The DCI paradigm is one example of how we can return closer to the original vision. It\u2019s about driving our system architecture in terms of end-user mental models rather than technology. It\u2019s about build domain-specific languages that enable end users to express themselves rather than custom-crafting each solution from general-purpose toolboxes. We must view the interaction with the end user with the same attentiveness we give to nerd interactions, and be more outward focused and inclusive of end-user perspectives.</p>\n'), TIMESTAMP '2016-05-16 17:45:00.0', '', TIMESTAMP '2016-05-16 16:45:00.0', 'How Agile and OO have lost their way together', 'Keynote');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(72, STRINGDECODE('<p>The Scrum Patterns tutorial is a highly acclaimed hands-on session where attendees learn how to use organisational patterns to diagnose problems on their Scrum teams and to gain insight on how to fix the problems. The core people in agile and Scrum have been codifying the underlying motivations and rationales for agile and Scrum for about twenty years now, branching out from work I initiated while at Bell Laboratories. One of the first outputs was the critically acclaimed Organisational Patterns book (<a href=\"http://www.amazon.de/Organizational-Patterns-Agile-Software-Development/dp/0131467409/\">http://www.amazon.de/Organizational-Patterns-Agile-Software-Development/dp/0131467409/</a>), and there have been many more since. A community of agile leaders carries on the work in a forum called Scrum PLoP\u00ae, and we have gathered some priceless insights from the agile illuminati in this community.</p>\n<p>This seminar teaches people what patterns are and familiarises them with how to navigate available patterns and how to use them in their everyday work. We have attendees list their teams\u2019 impediments and then have them discover what combination of patterns will help them resolve the tradeoffs in their organisation. They leave the seminar armed to concretely improve their own Agile processes.<br />\nThe seminar consistently gets high ratings from attendees.</p>\n'), NULL, 'Rebland', TIMESTAMP '2016-05-17 09:30:00.0', 'Scrum Patterns', 'Tutorial'),
(73, STRINGDECODE('<p>AngularJS wurde ausdr\u00fccklich mit dem Ziel entworfen, gut testbar zu sein. Dies bezieht sich auf die ganze Palette an Testarten, vom Oberfl\u00e4chentest bis zum Unittest.<br />\nIn der Praxis hat sich jedoch gezeigt, dass das Testen von Direktiven mittels Unittests schwer f\u00e4llt. Oft werden deshalb entweder gar keine Tests oder GUI-Tests daf\u00fcr geschrieben. Fehlen Tests besteht das Risiko, dass Fehler in der Anwendung nicht oder zu sp\u00e4t gefunden werden. Gibt es stattdessen Oberfl\u00e4chentests, wird die Weiterentwicklung durch die typischerweise hohe Testlaufzeit und Fragilit\u00e4t beeintr\u00e4chtigt.<br />\nUm diese Probleme zu vermeiden, nehmen wir uns in diesem Workshop den typischen Schwierigkeiten beim Testschreiben an. Wir werden in kleinen Gruppen praktische \u00dcbungen zu Teststrategien durchf\u00fchren und diskutieren. Konkret thematisiert wird das Testen von Komponenten mit externen Abh\u00e4ngigkeiten, der immer wichtiger werdende Umgang mit Asynchronit\u00e4t und dem Testen von Direktiven.</p>\n'), NULL, 'Pfinzgau', TIMESTAMP '2016-05-17 09:30:00.0', STRINGDECODE('AngularJS: Teststrategien f\u00fcr Fortgeschrittene'), 'Tutorial'),
(74, STRINGDECODE('<p>Das Tutorial zeigt den Weg von der Ger\u00e4teauswahl bis hin zu einem fertigen System anhand der Open Source L\u00f6sungen Eclipse Vorto zur Integration und der modellgetriebenen L\u00f6sung SensIDL zur Kommunikation. Es zielt insbesondere auf die Schnittstelle zwischen Entwicklern eingebetteter Systeme, mobiler Anwendungen, Cloud L\u00f6sungen sowie Architekten f\u00fcr IoT L\u00f6sungen.<br />\nEclipse Vorto erlaubt die Erstellung, das Management und die Ver\u00f6ffentlichung von Ger\u00e4tebeschreibungen (sogenannte Informationsmodelle). Mit Hilfe der Vorto Code Generator Infrastruktur k\u00f6nnen diese Informationsmodelle in andere Formate \u00fcberf\u00fchrt werden. Beispiele f\u00fcr solche Formate sind XML, Java Code oder auch Markdown und LaTeX. Dies erm\u00f6glicht unter Anderem die vollautomatische Erzeugung eines auf Eclipse Californium basierenden CoAP Servers und zugeh\u00f6rigen Clients, welche \u00fcber die im Informationsmodell beschriebenen Schnittstelle kommunizieren. SensIDL und Eclipse Vorto erg\u00e4nzen sich gegenseitig mit ihren unterschiedlichen Funktionalit\u00e4ten. Im Zusammenspiel erlauben die L\u00f6sungen die Nutzung ma\u00dfgeschneiderter Protokolle. Beispielsweise f\u00fcr energieeffiziente Sensoren oder Sensorsystemen ohne Profilunterst\u00fctzung in Funkprotokollen wie Bluetooth LE oder ZigBee.<br />\nIm Tutorial liegt der Fokus darauf an konkreten Beispielen und Ger\u00e4ten einen Einblick in die Entwicklung eines fertigen Systems zu bekommen und an welchen Stellen Werkzeuge Fehler und repetitive Arbeit reduzieren k\u00f6nnen.</p>\n'), NULL, 'Kraichgau', TIMESTAMP '2016-05-17 09:30:00.0', STRINGDECODE('Internet der Dinge \u2013 von der Ger\u00e4tbeschreibung zum fertigen System'), 'Tutorial');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(75, STRINGDECODE('<p>Bei den japanischen Kampfsportarten ist sie eine Jahrhunderte alte Tradition, bei Softwareentwicklern wird sie bereits seit etlichen Jahren praktiziert und f\u00fcr Softwarearchitektur hat sie Ted Neward auf der \u00dcberconf 2010 in Denver bekannt gemacht. Die Kata \u2013 eine \u00dcbungsform bei der dieselbe Aufgabe immer und immer wieder durchgef\u00fchrt wird, mit dem Ziel sich bei jeder Wiederholung zu verbessern.<br />\nIn diesem Workshop werden wir gemeinsam eine Architektur-Kata durchf\u00fchren. In kleinen Gruppen werden Sie zu einer gegebenen Problemstellung eine Softwarearchitektur erstellen und in Form von Diagrammen skizzieren. Dabei erfahren Sie, wie schwierig es sein kann, eine zu den Anforderungen passende Architektur zu erarbeiten. Sie werden erkennen, wie wichtig es ist, die richtigen Fragen an die Stakeholder zu stellen. Das Ergebnis ihrer Arbeit werden Sie den anderen Teilnehmern pr\u00e4sentieren und zur Diskussion stellen. Aus dem Feedback lernen Sie ihre Diagramme besser zu strukturieren und effektiver zu gestalten. Die passend eingestreute Vermittlung von Wissen zur Entwicklung von Softwarearchitekturen soll ihr Verst\u00e4ndnis f\u00f6rdern und ihre F\u00e4higkeiten erweitern. In einem zweiten Durchlauf der Kata, werden Sie die neuen Erkenntnisse direkt wieder in die Tat umsetzen k\u00f6nnen.<br />\nArchitektur-Katas lassen sich hervorragend zu Trainings- und Ausbildungszwecken einsetzen. Das Vorgehen l\u00e4sst sich aber auch in echten Projekten, z.B. zur gemeinsamen Erarbeitung einer Architekturvision in agilen Teams nutzen. Ein kleiner Ausblick soll Ihnen helfen das Erlernte auch in Ihren Projekten anwenden zu k\u00f6nnen.</p>\n'), NULL, 'Rheinauen', TIMESTAMP '2016-05-17 09:30:00.0', STRINGDECODE('Architektur-Kata als Trainingsform f\u00fcr agile Teams'), 'Tutorial'),
(76, STRINGDECODE('<p>Vielleicht hast du schon einmal von den SOLID-Prinzipien geh\u00f6rt. Vielleicht wei\u00dft du auch noch wof\u00fcr die Anfangsbuchstaben des Akronyms stehen:<br />\n- Single Responsibility<br />\n- Open/Closed<br />\n- Liskov Substitution<br />\n- Interface Segregation<br />\n- Dependency Inversion</p>\n<p>Zu verstehen was hinter den einzelnen Prinzipien steckt ist jedoch nicht selbstverst\u00e4ndlich. Dadurch wird h\u00e4ufig auch nicht erkannt, wenn eines der Prinzipien verletzt wird. Selbst Source Code zu entwickeln, der die Prinzipien erf\u00fcllt, oder bestehenden Source Code so zu \u00fcberarbeiten, dass er danach den Prinzipien entspricht, ist noch eine gr\u00f6\u00dfere Herausforderung.<br />\nAls Teilnehmer dieses Workshops hast du die Gelegenheit die SOLID-Prinzipien n\u00e4her kennenzulernen. In Programmier\u00fcbungen wirst du bestehenden schwer testbaren Legacy-Code einem Refactoring unterziehen, so dass er anschlie\u00dfend den SOLID-Prinzipien entspricht und du einfacher Unit-Tests daf\u00fcr schreiben kannst.<br />\nZu Beginn werden wir die SOLID-Prinzipien vorstellen und mit euch diskutieren. Anschlie\u00dfend legst du im Pair los und wirst die SOLID-Prinzipien hautnah erleben. Zwischen den einzelnen \u00dcbungen werden wir uns ausreichend Zeit nehmen um die gemachten Erfahrungen auszutauschen und zu besprechen.<br />\nNach dem Workshop hast du ein tieferes Verst\u00e4ndnis der SOLID-Prinzipien und hast damit den ersten Schritt gemacht um diese in deinem Arbeitsalltag anzuwenden.</p>\n<p>*Voraussetzungen*<br />\nDu solltest gute Kenntnisse in mindestens einer objektorientierten Programmiersprache (bevorzugt Java) haben und ein Notebook mit fertig eingerichteter Entwicklungsumgebung, inklusive Testing-Framework und lokalem Versionskontrollsystem, mitbringen.</p>\n'), NULL, 'Saal Baden', TIMESTAMP '2016-05-17 09:30:00.0', 'SOLID - Verstehen und Anwenden', 'Tutorial'),
(77, STRINGDECODE('<p>In vielen Software-Projekten wissen h\u00e4ufig nicht nur Anwender sondern auch Entwickler nicht warum sich ihre Anwendung verh\u00e4lt wie sie sich eben verh\u00e4lt. Wieso ist meine Anwendung so langsam? An Performance-Tests wurde w\u00e4hrend der Entwicklung nicht gespart. Was kann also das Problem sein? Abhilfe kann in solchen F\u00e4llen ein Application Performance Management Werkzeug schaffen, wie beispielsweise das Open Source-Werkzeug inspectIT.<br />\nDies ist ein leistungsstarkes Werkzeug, dass dazu dient, die Metriken von Software-Anwendungen zu \u00fcberwachen, sodass jederzeit Informationen \u00fcber die Anwendung verf\u00fcgbar sind, um jedes potenzielle Funktions-, Leistungs- oder Zuverl\u00e4ssigkeitsproblem analysieren zu k\u00f6nnen.<br />\nDieses Tutorial bietet einen einfachen Einstieg in die Performance-Analyse von Java-Anwendungen. Zu Beginn wird die Motivation f\u00fcr die Problematik Software-Performance vermittelt und die Teilnehmer werden mit den notwendigen Grundlagen des Themas vertraut gemacht. Anschlie\u00dfend wird ein technischer Blick vor und hinter die Kulissen von inspectIT geworfen um den Teilnehmern einen Einblick zu geben, wie das Tool funktioniert. Danach wird den Teilnehmern eine M\u00f6glichkeit aufgezeigt wie sie ein Tool zur Performancemessung in einen Anwendungsserver integrieren und konfigurieren um die gew\u00fcnschten Daten \u00fcber die Software-Anwendung zu erfassen. Ein gro\u00dfer Fokus wird auf die Analyse der Daten geworfen um die Teilnehmer in das Erkennen und Diagnostizieren von Problemen einzuf\u00fchren. Da im Projektalltag die Zusammenarbeit in den Bereichen Entwicklung und Betrieb immer mehr an Bedeutung gewinnt (Stichwort \u201eDevOps\u201c), zeigen wir wie eine interaktive Probleml\u00f6sung stattfinden kann.<br /><a href=\"https://github.com/inspectIT/inspectIT\">https://github.com/inspectIT/inspectIT</a></p>\n'), NULL, 'Schwarzwald', TIMESTAMP '2016-05-17 09:30:00.0', 'Open Source Application Performance Monitoring for the Crowd: inspectIT', 'Tutorial');
INSERT INTO PUBLIC.EVENT(ID, DESCRIPTION, END_TIME, ROOM, START_TIME, TITLE, TRACK) VALUES
(78, STRINGDECODE('<p>Java 8 stellt die gr\u00f6\u00dfte \u00c4nderung an der Programmiersprache dar seit der Einf\u00fchrung von Generics mit Java 5. F\u00fcr Java 7 gibt es seit April 2015 keine \u00f6ffentlichen Updates mehr. Trotzdem ist Java 8 bisher noch nicht in allen Projekten angekommen. Die neuen Sprachfeatures tauchen nach unserer Beobachtung beim t\u00e4glichen Code-Schreiben nicht auf.<br />\nMit diesem Workshop m\u00f6chten wir allen, die bisher noch nicht die Gelegenheit dazu hatten, einen Ein- und \u00dcberblick geben \u00fcber Java 8 Features wie Lambdas, die Stream-API, die Optional-Klasse und die DateTime-API. Die Teilnehmer k\u00f6nnen ausprobieren, wie einige Praktiken aus der funktionalen Programmierung in Java Einzug finden und welche weiteren Sprachfeatures das t\u00e4gliche Arbeiten mit Java erleichtern k\u00f6nnen. Der Hauptteil des Workshops besteht aus praktischen \u00dcbungen, erg\u00e4nzt durch Theorie-Teile und Live-Demos.</p>\n<p>Die Teilnehmer sollten je zu zweit einen Laptop mit JDK 8 und einer Java-IDE (vorzugsweise eclipse) dabei haben. Eine paarweise Anmeldung ist jedoch nicht notwendig. Grundlegende Java-Kenntnisse sollten vorhanden sein.</p>\n'), NULL, 'Fidelitas', TIMESTAMP '2016-05-17 09:30:00.0', 'Java 8 Workshop', 'Tutorial');
