INSERT INTO PUBLIC.SPEAKER (ID, BIOGRAPHY, COMPANY, FIRST_NAME, LAST_NAME) VALUES
  (1, STRINGDECODE(
      'Robert Br\u00e4utigam ist seit 1999 Java, Java Enterprise Entwickler mit Rollen von Entwickler in Kleinprojekten bis zu Lead-Architekt auf internationalen Gro\u00dfprojekten. Er sucht nach, und argumentiert leidenschaftlich f\u00fcr alternative, leichtgewichtige L\u00f6sungen im technischen Bereich sowie im Projektmanagement.'),
   'MATHEMA GmbH', 'Robert', STRINGDECODE('Br\u00e4utigam')),
  (2, STRINGDECODE(
      'Simon Wagner arbeitet seit 2013 als Software-Entwickler f\u00fcr die andrena objects ag. Dabei setzt er gerne auf agile Methoden wie Scrum und XP. Er interessiert sich besonders f\u00fcr Clean Code und testgetriebene Entwicklung.'),
   'andrena objects ag', 'Simon', 'Wagner'),
  (3, STRINGDECODE(
      'Hartmut Senska blickt auf eine mehrj\u00e4hrige Erfahrung im agilen Umfeld zur\u00fcck und arbeitet seit 2014 als Agile Coach und Scrum Master f\u00fcr die andrena objects ag.\nSeit anderthalb Jahren begleitet er einen Kunden dabei seine Projekte agil zu skalieren und deren Erfolg durch kontinuierliche Professionalisierung sicherzustellen.'),
   'andrena objects ag', 'Hartmut', 'Senska'),
  (4, STRINGDECODE(
      'Johannes Hoppe ist selbstst\u00e4ndiger IT-Berater, Softwareentwickler und Trainer.\nEr realisiert seit mehr als 10 Jahren Software-Projekte f\u00fcr das Web und entwickelt moderne Portale auf Basis von .NET und Angular. Johannes Hoppe ist Leiter der .NET User Group Rhein-Neckar und unterrichtet als Lehrbeauftragter der der Dualen Hochschule Baden-W\u00fcrttemberg. Er schreibt \u00fcber seine Vorlesungen, Trainings und Vortr\u00e4ge in seinem Blog. (http://blog.johanneshoppe.de/)'),
   'HAUS HOPPE - ITS', 'Johannes', 'Hoppe'),
  (5, STRINGDECODE(
      'Jeroen Laverman ist Mitarbeiter der Firma Bosch Software Innovations GmbH. Im Rahmen seiner T\u00e4tigkeit als Consultant ist er unter anderem im Open Source Project Eclipse Vorto aktiv, welches von Bosch Software Innovations im Jahr 2014 initiiert wurde.'),
   'Bosch Software Innovations GmbH', 'Jeroen', 'Laverman'),
  (6,
   'Martin Engel works as Development Project Expert in SAP''s User Interface Technology Development area. Initially a C/C++ software developer and quality manager, in recent years his focus has shifted to shaping the transition towards Lean and Agile. He is a certified Scrum Master and started using agile development methods back in 2006. He began applying Kanban in his own team some five years ago, in addition to supporting other teams with training, coaching and mentoring. His current focus is on supporting the overall scaled Lean/agile setup in the UI development area.',
   'SAP AG', 'Dr. Martin', 'Engel'),
  (7,
   'Dr. Mana Taghdiri is the lead software developer at Horus GmbH. She has received her Masters and PhD degrees in the field of Computer Science, Software Design from MIT in Massachusetts, USA. She has worked as a senior software engineer on the design of a Just-In-Time compiler at MathWorks Inc, and later joined KIT as a junior professor in Informatics, supervising the Automated Software Analysis group. She has several publications on automatic software checking and requirements analysis. She has received two ACM/SIGSOFT distinguished paper awards for her work on scalable software analysis, and research grants for her ideas on automating software verification.',
   'Horus software GmbH', 'Dr. Mana', 'Taghdiri'),
  (8, STRINGDECODE(
      'Pascal Giessler ist Promotionsstudent der Informatik in der Forschungsgruppe Cooperation & Management (C&M, Prof. Abeck) am Karlsruher Institut f\u00fcr Technologie (KIT). Sein Schwerpunkt ist die qualit\u00e4tsgesicherte Entwicklung von ressourcenorientierten Services. Hierzu hat er bereits mehrere Publikationen sowohl auf internationalen Konferenzen als auch in Fachmagazinen ver\u00f6ffentlicht. Daneben arbeitet er als Software-Ingenieur und -Architekt f\u00fcr international agierende Unternehmen, ist im Bereich der Forschung und Entwicklung in einem Unternehmen f\u00fcr digitale Innovationen t\u00e4tig und ist Ko-Dozent der Lehrveranstaltungen \"Web-Anwendungen und serviceorientierte Architekturen\" am KIT.'),
   'ZWEI14 Digital GmbH', 'Pascal', 'Giessler');
INSERT INTO PUBLIC.SPEAKER (ID, BIOGRAPHY, COMPANY, FIRST_NAME, LAST_NAME) VALUES
  (9, STRINGDECODE(
      'Timo Fink ist Gesch\u00e4ftsf\u00fchrer der ZWEI14 Digital GmbH und verantwortet dort den Bereich Infrastruktur und Software Engineering. Nach langj\u00e4hriger Erfahrung in den Entwicklungsabteilungen gro\u00dfer Konzerne wie beispielsweise T-Systems und der KPMG leitet er die Entwicklerteams im Enterprise Kundensegment von ZWEI14 und ist verantwortlich f\u00fcr Technologieentscheidungen in Front- und Backend-Systemen.'),
   'ZWEI14 Digital GmbH', 'Timo', 'Fink'),
  (10, STRINGDECODE(
